'use client';

import { useEffect, useState } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { Button } from '@/components/ui/button';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from '@/components/ui/form';
import { Input } from '@/components/ui/input';
import { RadioGroup, RadioGroupItem } from '@/components/ui/radio-group';
import { Label } from '@/components/ui/label';
import { useToast } from '@/hooks/use-toast';
import { createUser } from '@/lib/admin-actions';
import { Loader2 } from 'lucide-react';

const formSchema = z.object({
  name: z.string().min(2, 'Name must be at least 2 characters.'),
  email: z.string().email('Invalid email address.'),
  role: z.enum(['User', 'Admin']),
});

type AddUserFormValues = z.infer<typeof formSchema>;

type AddUserDialogProps = {
  isOpen: boolean;
  onClose: () => void;
  onUserAdded: () => void;
  prefilledData?: { name: string; email: string; submissionId?: string } | null;
};

export default function AddUserDialog({ isOpen, onClose, onUserAdded, prefilledData }: AddUserDialogProps) {
  const { toast } = useToast();
  const [isLoading, setIsLoading] = useState(false);
  const form = useForm<AddUserFormValues>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      name: '',
      email: '',
      role: 'User',
    },
  });

  useEffect(() => {
    if (prefilledData) {
      form.reset({
        name: prefilledData.name,
        email: prefilledData.email,
        role: 'User',
      });
    } else {
      form.reset({
        name: '',
        email: '',
        role: 'User'
      });
    }
  }, [prefilledData, form, isOpen]);


  const onSubmit = async (values: AddUserFormValues) => {
    setIsLoading(true);
    try {
      const result = await createUser(values, prefilledData?.submissionId);
      if (result.success) {
        toast({
          title: 'Success',
          description: 'Invitation email sent to the new user.',
        });
        onUserAdded();
        form.reset();
      } else {
        throw new Error(result.message || 'Failed to create user.');
      }
    } catch (error) {
      toast({
        variant: 'destructive',
        title: 'Error',
        description: error instanceof Error ? error.message : 'An unexpected error occurred.',
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-[425px]">
        <DialogHeader>
          <DialogTitle>Add New User</DialogTitle>
          <DialogDescription>
            An invitation will be sent to the user's email to set up their account.
          </DialogDescription>
        </DialogHeader>
        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <FormField
              control={form.control}
              name="name"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Full Name</FormLabel>
                  <FormControl>
                    <Input placeholder="John Doe" {...field} />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />
            <FormField
              control={form.control}
              name="email"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Email</FormLabel>
                  <FormControl>
                    <Input placeholder="user@example.com" {...field} />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />
            <FormField
              control={form.control}
              name="role"
              render={({ field }) => (
                <FormItem className="space-y-3">
                  <FormLabel>Role</FormLabel>
                  <FormControl>
                    <RadioGroup
                      onValueChange={field.onChange}
                      defaultValue={field.value}
                      className="flex space-x-4"
                    >
                      <FormItem className="flex items-center space-x-2">
                        <FormControl>
                          <RadioGroupItem value="User" />
                        </FormControl>
                        <Label className="font-normal">User</Label>
                      </FormItem>
                      <FormItem className="flex items-center space-x-2">
                        <FormControl>
                          <RadioGroupItem value="Admin" />
                        </FormControl>
                        <Label className="font-normal">Admin</Label>
                      </FormItem>
                    </RadioGroup>
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />
            <DialogFooter>
              <Button type="button" variant="ghost" onClick={onClose} disabled={isLoading}>
                Cancel
              </Button>
              <Button type="submit" disabled={isLoading}>
                {isLoading && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
                Send Invitation
              </Button>
            </DialogFooter>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

src/components/admin-inbox.tsx.content:
'use client';

import { useEffect, useState } from 'react';
import { useRouter } from 'next/navigation';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { Button } from '@/components/ui/button';
import { useToast } from '@/hooks/use-toast';
import { getSubmissions, sendReply, AdminSubmission, deleteSubmission, rejectSubmission } from '@/lib/admin-actions';
import { Badge } from '@/components/ui/badge';
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle, AlertDialogTrigger } from '@/components/ui/alert-dialog';
import { Textarea } from '@/components/ui/textarea';
import { Loader2, Inbox, Send, Eye, CheckCircle, XCircle, Trash2 } from 'lucide-react';
import { Label } from './ui/label';

export default function AdminInbox() {
  const router = useRouter();
  const [submissions, setSubmissions] = useState<AdminSubmission[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isActionLoading, setIsActionLoading] = useState<string | null>(null);
  const [selectedSubmission, setSelectedSubmission] = useState<AdminSubmission | null>(null);
  const [isViewOpen, setIsViewOpen] = useState(false);
  const [isReplyOpen, setIsReplyOpen] = useState(false);
  const [replyMessage, setReplyMessage] = useState('');
  const [isReplying, setIsReplying] = useState(false);
  const { toast } = useToast();

  const fetchSubmissions = async () => {
    setIsLoading(true);
    try {
      const data = await getSubmissions();
      setSubmissions(data);
    } catch (error) {
      toast({
        variant: 'destructive',
        title: 'Error',
        description: error instanceof Error ? error.message : 'Failed to fetch submissions.',
      });
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchSubmissions();
  }, []);

  const handleAction = async (action: () => Promise<any>, submissionId: string, successMessage: string) => {
    setIsActionLoading(submissionId);
    try {
        const result = await action();
        if (result.success) {
            toast({ title: 'Success', description: successMessage });
            fetchSubmissions();
        } else {
            throw new Error(result.message);
        }
    } catch (error) {
        toast({
            variant: 'destructive',
            title: 'Error',
            description: error instanceof Error ? error.message : 'An unexpected error occurred.',
        });
    } finally {
        setIsActionLoading(null);
    }
  }

  const handleApprove = (submission: AdminSubmission) => {
    // Redirect to admin page with query params to open the Add User dialog
    const approveUrl = `/admin?action=addUser&name=${encodeURIComponent(submission.name)}&email=${encodeURIComponent(submission.email)}&submissionId=${submission.id}`;
    router.push(approveUrl);
  };

  const handleReject = async (submissionId: string) => {
    await handleAction(() => rejectSubmission(submissionId), submissionId, 'Access request rejected.');
  };

  const handleDelete = async (submissionId: string) => {
    await handleAction(() => deleteSubmission(submissionId), submissionId, 'Submission deleted.');
  };

  const handleView = (submission: AdminSubmission) => {
    setSelectedSubmission(submission);
    setIsViewOpen(true);
  };

  const handleReply = (submission: AdminSubmission) => {
    setSelectedSubmission(submission);
    setIsReplyOpen(true);
  };

  const handleSendReply = async () => {
    if (!selectedSubmission || !replyMessage.trim()) return;
    setIsReplying(true);
    try {
      const result = await sendReply(selectedSubmission.id, replyMessage);
      if (result.success) {
        toast({ title: 'Success', description: 'Reply sent successfully.' });
        setIsReplyOpen(false);
        setReplyMessage('');
        fetchSubmissions(); // Refresh the list to show 'Replied' status
      } else {
        throw new Error(result.message);
      }
    } catch (error) {
      toast({
        variant: 'destructive',
        title: 'Error',
        description: error instanceof Error ? error.message : 'Failed to send reply.',
      });
    } finally {
      setIsReplying(false);
    }
  };

  const getStatusVariant = (status: AdminSubmission['status']): 'default' | 'secondary' | 'destructive' | 'outline' => {
      switch(status) {
          case 'Approved': return 'default';
          case 'Pending': return 'secondary';
          case 'Rejected': return 'destructive';
          case 'Replied': return 'outline';
          default: return 'secondary';
      }
  }

  return (
    <>
      <Card>
        <CardHeader className="flex items-center gap-2 flex-row">
          <Inbox />
          <CardTitle>Inbox</CardTitle>
        </CardHeader>
        <CardContent>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Date</TableHead>
                <TableHead>From</TableHead>
                <TableHead>Type</TableHead>
                <TableHead>Status</TableHead>
                <TableHead className="text-right">Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {isLoading ? (
                <TableRow>
                  <TableCell colSpan={5} className="text-center">
                    <Loader2 className="mx-auto my-4 h-6 w-6 animate-spin" />
                  </TableCell>
                </TableRow>
              ) : submissions.map((sub) => (
                <TableRow key={sub.id}>
                  <TableCell>{new Date(sub.createdAt).toLocaleDateString()}</TableCell>
                  <TableCell>{sub.email}</TableCell>
                  <TableCell>{sub.type}</TableCell>
                  <TableCell>
                    <Badge variant={getStatusVariant(sub.status)}>{sub.status}</Badge>
                  </TableCell>
                  <TableCell className="space-x-1 text-right">
                    {isActionLoading === sub.id ? <Loader2 className="h-5 w-5 animate-spin inline-flex" /> : (
                      <>
                        <Button variant="outline" size="sm" onClick={() => handleView(sub)}>
                          <Eye className="mr-2 h-4 w-4"/> View
                        </Button>

                        {sub.type === 'Access Request' && sub.status === 'Pending' && (
                            <>
                                <Button size="sm" onClick={() => handleApprove(sub)} variant="secondary">
                                    <CheckCircle className="mr-2 h-4 w-4"/> Approve
                                </Button>
                                <AlertDialog>
                                    <AlertDialogTrigger asChild>
                                        <Button size="sm" variant="destructive">
                                            <XCircle className="mr-2 h-4 w-4"/> Reject
                                        </Button>
                                    </AlertDialogTrigger>
                                    <AlertDialogContent>
                                        <AlertDialogHeader>
                                            <AlertDialogTitle>Reject Access Request?</AlertDialogTitle>
                                            <AlertDialogDescription>An email will be sent to the user informing them of the rejection. Are you sure?</AlertDialogDescription>
                                        </AlertDialogHeader>
                                        <AlertDialogFooter>
                                            <AlertDialogCancel>Cancel</AlertDialogCancel>
                                            <AlertDialogAction onClick={() => handleReject(sub.id)} className="bg-destructive hover:bg-destructive/90">Reject</AlertDialogAction>
                                        </AlertDialogFooter>
                                    </AlertDialogContent>
                                </AlertDialog>
                            </>
                        )}

                        {sub.type === 'General Inquiry' && sub.status !== 'Replied' && (
                             <Button size="sm" onClick={() => handleReply(sub)}>
                                <Send className="mr-2 h-4 w-4"/> Reply
                            </Button>
                        )}

                         <AlertDialog>
                            <AlertDialogTrigger asChild>
                                <Button variant="ghost" size="icon" className="h-8 w-8">
                                    <Trash2 className="h-4 w-4" />
                                </Button>
                            </AlertDialogTrigger>
                            <AlertDialogContent>
                                <AlertDialogHeader>
                                    <AlertDialogTitle>Delete Submission?</AlertDialogTitle>
                                    <AlertDialogDescription>This will permanently delete this submission. This action cannot be undone.</AlertDialogDescription>
                                </AlertDialogHeader>
                                <AlertDialogFooter>
                                    <AlertDialogCancel>Cancel</AlertDialogCancel>
                                    <AlertDialogAction onClick={() => handleDelete(sub.id)} className="bg-destructive hover:bg-destructive/90">Delete</AlertDialogAction>
                                </AlertDialogFooter>
                            </AlertDialogContent>
                        </AlertDialog>
                      </>
                    )}
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </CardContent>
      </Card>

      {/* View Details Dialog */}
      <Dialog open={isViewOpen} onOpenChange={setIsViewOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Submission Details</DialogTitle>
          </DialogHeader>
          {selectedSubmission && (
            <div className="space-y-4 py-4">
              <div><strong>From:</strong> {selectedSubmission.name} ({selectedSubmission.email})</div>
              <div><strong>Date:</strong> {new Date(selectedSubmission.createdAt).toLocaleString()}</div>
              <div><strong>Type:</strong> {selectedSubmission.type}</div>
              <div><strong>Status:</strong> <Badge variant={getStatusVariant(selectedSubmission.status)}>{selectedSubmission.status}</Badge></div>
              <div className="space-y-1">
                  <Label>Message:</Label>
                  <p className="p-3 bg-muted rounded-md text-sm">{selectedSubmission.message}</p>
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>

      {/* Reply Dialog */}
      <Dialog open={isReplyOpen} onOpenChange={setIsReplyOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Reply to {selectedSubmission?.name}</DialogTitle>
            <DialogDescription>Your reply will be sent via email to {selectedSubmission?.email}.</DialogDescription>
          </DialogHeader>
          <div className="py-4 space-y-4">
             <div className="space-y-1">
                <Label>Original Message:</Label>
                <p className="p-3 bg-muted rounded-md text-sm">{selectedSubmission?.message}</p>
            </div>
            <Textarea
              placeholder="Type your reply here..."
              value={replyMessage}
              onChange={(e) => setReplyMessage(e.target.value)}
              rows={6}
            />
          </div>
          <DialogFooter>
            <Button variant="ghost" onClick={() => setIsReplyOpen(false)}>Cancel</Button>
            <Button onClick={handleSendReply} disabled={isReplying || !replyMessage.trim()}>
              {isReplying && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
              Send Reply
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </>
  );
}

src/components/admin-settings.tsx.content:
'use client';

import { useEffect, useState } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { useToast } from '@/hooks/use-toast';
import { getVoiceProviderSettings, saveVoiceProviderSettings, type VoiceProviderSettings } from '@/lib/admin-actions';
import { getAllVoiceProviders } from '@/ai/flows/voice-selection';
import { Label } from '@/components/ui/label';
import { Switch } from '@/components/ui/switch';
import { Loader2 } from 'lucide-react';

export default function AdminSettings() {
  const [settings, setSettings] = useState<VoiceProviderSettings>({});
  const [isLoading, setIsLoading] = useState(true);
  const [isSaving, setIsSaving] = useState(false);
  const [providers, setProviders] = useState<string[]>([]);
  const { toast } = useToast();

  useEffect(() => {
    async function fetchSettings() {
      setIsLoading(true);
      try {
        const availableProviders = getAllVoiceProviders();
        setProviders(availableProviders);

        const savedSettings = await getVoiceProviderSettings();
        // Initialize settings for any provider not in the database
        const initialSettings: VoiceProviderSettings = {};
        availableProviders.forEach(provider => {
            initialSettings[provider] = savedSettings[provider] ?? true; // Default to true if not set
        });
        setSettings(initialSettings);
      } catch (error) {
        toast({
          variant: 'destructive',
          title: 'Error',
          description: error instanceof Error ? error.message : 'Failed to fetch settings.',
        });
      } finally {
        setIsLoading(false);
      }
    }
    fetchSettings();
  }, [toast]);

  const handleToggle = (provider: string, checked: boolean) => {
    setSettings(prev => ({ ...prev, [provider]: checked }));
  };

  const handleSaveChanges = async () => {
    setIsSaving(true);
    try {
      const result = await saveVoiceProviderSettings(settings);
      if (result.success) {
        toast({ title: 'Success', description: 'Settings have been saved.' });
      } else {
        throw new Error(result.message);
      }
    } catch (error) {
      toast({
        variant: 'destructive',
        title: 'Error',
        description: error instanceof Error ? error.message : 'Failed to save settings.',
      });
    } finally {
      setIsSaving(false);
    }
  };

  if (isLoading) {
    return (
      <div className="flex justify-center items-center p-8">
        <Loader2 className="h-8 w-8 animate-spin text-primary" />
      </div>
    );
  }

  return (
    <div className="grid gap-6">
      <Card>
        <CardHeader>
          <CardTitle>Voice Provider Management</CardTitle>
          <CardDescription>
            Enable or disable text-to-speech providers for all users. Changes take effect immediately.
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          {providers.map(provider => (
            <div key={provider} className="flex items-center justify-between p-4 border rounded-lg">
              <Label htmlFor={`provider-${provider}`} className="text-lg capitalize">
                {provider}
              </Label>
              <Switch
                id={`provider-${provider}`}
                checked={settings[provider]}
                onCheckedChange={(checked) => handleToggle(provider, checked)}
              />
            </div>
          ))}
        </CardContent>
      </Card>

      <div className="flex justify-end">
        <Button onClick={handleSaveChanges} disabled={isSaving}>
          {isSaving && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
          Save Changes
        </Button>
      </div>
    </div>
  );
}

src/components/ai-dialog.tsx.content:
'use client';

import React, { useState } from 'react';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogDescription,
} from '@/components/ui/dialog';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Button } from '@/components/ui/button';
import { Loader2, Pause, Volume2 } from 'lucide-react';
import type { SummarizePdfOutput } from '@/ai/flows/summarize-pdf';
import type { GenerateGlossaryOutput } from '@/ai/flows/glossary-flow';
import type { GenerateQuizOutput, QuizQuestion } from '@/ai/schemas/quiz';
import { ScrollArea } from './ui/scroll-area';
import { Accordion, AccordionContent, AccordionItem, AccordionTrigger } from './ui/accordion';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from './ui/card';
import { RadioGroup, RadioGroupItem } from './ui/radio-group';
import { Label } from './ui/label';
import { cn } from '@/lib/utils';
import ReactMarkdown from 'react-markdown';
import { QuizAttempt } from '@/lib/db';

export type AiDialogType = 'summary' | 'key-points' | 'glossary' | 'quiz';

type AiDialogProps = {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  type: AiDialogType;
  isLoading: boolean;
  summaryOutput: SummarizePdfOutput | null;
  glossaryOutput: GenerateGlossaryOutput | null;
  quizOutput: GenerateQuizOutput | null;
  quizAttempt: QuizAttempt | null;
  onQuizSubmit: (questions: QuizQuestion[], answers: Record<number, string>) => void;
  onPlayAudio: (text: string) => void;
  isPlaying: boolean;
};

const AiDialog: React.FC<AiDialogProps> = ({
  open,
  onOpenChange,
  type,
  isLoading,
  summaryOutput,
  glossaryOutput,
  quizOutput,
  quizAttempt,
  onQuizSubmit,
  onPlayAudio,
  isPlaying,
}) => {

  const renderLoading = (text: string) => (
    <div className="flex items-center justify-center space-x-2 h-full p-8">
      <Loader2 className="animate-spin" />
      <span>{text}</span>
    </div>
  )

  const renderSummaryContent = () => (
    <>
      <DialogHeader>
        <DialogTitle>AI Analysis</DialogTitle>
        <DialogDescription>
          Here's a summary and key points generated by the AI based on the document's content.
        </DialogDescription>
      </DialogHeader>
      <Tabs defaultValue={type === 'key-points' ? 'key-points' : 'summary'} className="w-full pt-4">
        <TabsList className="grid w-full grid-cols-2">
          <TabsTrigger value="summary">Summary</TabsTrigger>
          <TabsTrigger value="key-points">Key Points</TabsTrigger>
        </TabsList>
        <ScrollArea className="h-96">
          <TabsContent value="summary" className="p-4">
              <div className="flex justify-between items-center mb-2">
                <h3 className="text-lg font-semibold">Summary</h3>
                <Button variant="ghost" size="icon" onClick={() => onPlayAudio(summaryOutput?.summary || '')} disabled={!summaryOutput?.summary}>
                    {isPlaying ? <Pause className="h-5 w-5" /> : <Volume2 className="h-5 w-5" />}
                </Button>
              </div>
            {isLoading ? renderLoading('Generating summary...') : (
              <p className="text-sm text-muted-foreground whitespace-pre-wrap">
                {summaryOutput?.summary || 'No summary available.'}
              </p>
            )}
          </TabsContent>
          <TabsContent value="key-points" className="p-4">
            <div className="flex justify-between items-center mb-2">
                <h3 className="text-lg font-semibold">Key Points</h3>
                <Button variant="ghost" size="icon" onClick={() => onPlayAudio(summaryOutput?.keyPoints?.join('. ') || '')} disabled={!summaryOutput?.keyPoints}>
                    {isPlaying ? <Pause className="h-5 w-5" /> : <Volume2 className="h-5 w-5" />}
                </Button>
            </div>
            {isLoading ? renderLoading('Extracting key points...') : (
              <ul className="list-disc pl-5 space-y-2 text-sm text-muted-foreground">
                {summaryOutput?.keyPoints?.map((point, index) => (
                  <li key={index}>{point}</li>
                )) || <li>No key points available.</li>}
              </ul>
            )}
          </TabsContent>
        </ScrollArea>
      </Tabs>
    </>
  );

  const renderGlossaryContent = () => (
    <>
        <DialogHeader>
            <DialogTitle>Glossary</DialogTitle>
            <DialogDescription>Key terms and concepts identified from the document by the AI.</DialogDescription>
        </DialogHeader>
        <ScrollArea className="h-96 pr-4">
            {isLoading ? renderLoading('Creating glossary...') : (
                 <Accordion type="single" collapsible className="w-full">
                    {glossaryOutput?.glossary?.map((item, index) => (
                        <AccordionItem value={`item-${index}`} key={index}>
                            <AccordionTrigger>{item.term}</AccordionTrigger>
                            <AccordionContent>
                                <div className="flex justify-between items-start">
                                    <p className="flex-1 pr-4">{item.definition}</p>
                                    <Button variant="ghost" size="icon" onClick={() => onPlayAudio(item.definition)} disabled={!item.definition}>
                                        {isPlaying ? <Pause className="h-5 w-5" /> : <Volume2 className="h-5 w-5" />}
                                    </Button>
                                </div>
                            </AccordionContent>
                        </AccordionItem>
                    ))}
                </Accordion>
            )}
        </ScrollArea>
    </>
  );

  const QuizView: React.FC<{ questions: QuizQuestion[] }> = ({ questions }) => {
    const [answers, setAnswers] = useState<Record<number, string>>(quizAttempt?.answers || {});
    const [submitted, setSubmitted] = useState(!!quizAttempt);

    const handleAnswerChange = (questionIndex: number, value: string) => {
      setAnswers(prev => ({ ...prev, [questionIndex]: value }));
    };

    const handleSubmit = () => {
      onQuizSubmit(questions, answers);
      setSubmitted(true);
    };

    const getResultColor = (index: number, option?: string) => {
        if (!submitted) return '';
        const question = questions[index];
        const userAnswer = quizAttempt?.answers[index] || answers[index];
        const isCorrect = question.answer === userAnswer;

        if (option) { 
            if (option === question.answer) return 'text-green-700 font-bold';
            if (option === userAnswer && !isCorrect) return 'text-red-700 font-bold';
        } else {
            return isCorrect ? 'text-green-700' : 'text-red-700';
        }
        return '';
    }

    return (
      <div className="space-y-6">
        {questions.map((q, index) => (
          <Card key={index}>
            <CardHeader>
              <div className="flex justify-between items-center">
                <CardTitle>Question {index + 1}</CardTitle>
                 <Button variant="ghost" size="icon" onClick={() => onPlayAudio(q.question)} disabled={!q.question}>
                    {isPlaying ? <Pause className="h-5 w-5" /> : <Volume2 className="h-5 w-5" />}
                </Button>
              </div>
            </CardHeader>
            <CardContent className="space-y-4">
              <p>{q.question}</p>
              {q.type === 'multiple-choice' && (
                <RadioGroup 
                    onValueChange={(value) => handleAnswerChange(index, value)}
                    disabled={submitted}
                    value={answers[index]}
                >
                  {q.options?.map((option, i) => (
                    <div key={i} className="flex items-center space-x-2">
                      <RadioGroupItem value={option} id={`q${index}-o${i}`} />
                      <Label htmlFor={`q${index}-o${i}`} className={cn(getResultColor(index, option))}>
                        {option}
                      </Label>
                    </div>
                  ))}
                </RadioGroup>
              )}
              {q.type === 'true-false' && (
                 <RadioGroup 
                    onValueChange={(value) => handleAnswerChange(index, value)}
                    disabled={submitted}
                    value={answers[index]}
                >
                    <div className="flex items-center space-x-2">
                      <RadioGroupItem value="True" id={`q${index}-true`} />
                      <Label htmlFor={`q${index}-true`} className={cn(getResultColor(index, "True"))}>True</Label>
                    </div>
                    <div className="flex items-center space-x-2">
                      <RadioGroupItem value="False" id={`q${index}-false`} />
                      <Label htmlFor={`q${index}-false`} className={cn(getResultColor(index, "False"))}>False</Label>
                    </div>
                </RadioGroup>
              )}
              {submitted && (
                 <div className={cn("p-4 rounded-md text-sm bg-muted prose dark:prose-invert max-w-none", getResultColor(index))}>
                    <div className="flex justify-between items-start">
                        <div className="flex-1 pr-4">
                            <p className="font-bold">Correct Answer: {q.answer}</p>
                            <ReactMarkdown>{q.explanation}</ReactMarkdown>
                        </div>
                         <Button variant="ghost" size="icon" onClick={() => onPlayAudio(`Correct answer: ${q.answer}. ${q.explanation}`)} disabled={!q.explanation}>
                            {isPlaying ? <Pause className="h-5 w-5" /> : <Volume2 className="h-5 w-5" />}
                        </Button>
                    </div>
                 </div>
              )}
            </CardContent>
          </Card>
        ))}
        {!submitted && (
            <Button onClick={handleSubmit} className="w-full">Submit Answers</Button>
        )}
        {submitted && quizAttempt && (
            <Card>
                <CardHeader>
                     <div className="flex justify-between items-center">
                        <div>
                            <CardTitle>Quiz Results & Suggestions</CardTitle>
                            <CardDescription>Your Score: {quizAttempt.score.toFixed(0)}%</CardDescription>
                        </div>
                        <Button variant="ghost" size="icon" onClick={() => onPlayAudio(quizAttempt.suggestions)} disabled={!quizAttempt.suggestions}>
                            {isPlaying ? <Pause className="h-5 w-5" /> : <Volume2 className="h-5 w-5" />}
                        </Button>
                    </div>
                </CardHeader>
                <CardContent className="prose dark:prose-invert max-w-none">
                    <ReactMarkdown>{quizAttempt.suggestions}</ReactMarkdown>
                </CardContent>
            </Card>
        )}
      </div>
    );
  };

  const renderQuizContent = () => (
    <>
        <DialogHeader>
            <DialogTitle>AI-Generated Quiz</DialogTitle>
            <DialogDescription>Test your knowledge with these questions based on the document.</DialogDescription>
        </DialogHeader>
        <ScrollArea className="h-[32rem] pr-4 mt-4">
            {isLoading ? renderLoading('Generating your quiz...') : (
                (quizOutput?.quiz || quizAttempt?.questions) && <QuizView questions={quizOutput?.quiz || quizAttempt!.questions} />
            )}
        </ScrollArea>
    </>
  );

  const renderContent = () => {
    switch (type) {
        case 'summary':
        case 'key-points':
            return renderSummaryContent();
        case 'glossary':
            return renderGlossaryContent();
        case 'quiz':
            return renderQuizContent();
        default:
            return null;
    }
  }

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-3xl">
        {renderContent()}
      </DialogContent>
    </Dialog>
  );
};

export default AiDialog;

src/components/audio-player.tsx.content:
'use client';

import React from 'react';
import { Button } from '@/components/ui/button';
import { Play, Pause, Loader2, Download, Wind, FastForward, Rewind, ZoomIn, ZoomOut, Maximize, Minimize, Save, AlertCircle } from 'lucide-react';
import { Card } from './ui/card';
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from './ui/dropdown-menu';
import { Slider } from './ui/slider';
import { Separator } from './ui/separator';
import { Tooltip, TooltipContent, TooltipTrigger } from './ui/tooltip';
import { useMediaQuery } from '@/hooks/use-media-query';
import type { AudioGenerationStatus } from '@/lib/db';
import { cn } from '@/lib/utils';


type AudioPlayerProps = {
  isSpeaking: boolean;
  docStatus: AudioGenerationStatus;
  onPlayPause: () => void;
  canPlay: boolean;
  playbackRate: number;
  onPlaybackRateChange: (rate: number) => void;
  showDownload: boolean;
  downloadUrl: string;
  downloadFileName: string;
  progress: number;
  duration: number;
  currentTime: number;
  onSeek: (value: number) => void;
  onForward: () => void;
  onRewind: () => void;
  isPdfLoaded: boolean;
  zoomLevel: number;
  onZoomIn: () => void;
  onZoomOut: () => void;
  isFullScreen: boolean;
  onFullScreenToggle: () => void;
  onSaveZoom: () => void;
  isSavingZoom: boolean;
};

const playbackRates = [0.75, 1.0, 1.25, 1.5, 2.0];

const formatTime = (seconds: number): string => {
    if (isNaN(seconds) || seconds < 0) return '00:00';
    const floorSeconds = Math.floor(seconds);
    const min = Math.floor(floorSeconds / 60);
    const sec = floorSeconds % 60;
    return `${min.toString().padStart(2, '0')}:${sec.toString().padStart(2, '0')}`;
};

const AudioPlayer: React.FC<AudioPlayerProps> = ({
  isSpeaking,
  docStatus,
  onPlayPause,
  canPlay,
  playbackRate,
  onPlaybackRateChange,
  showDownload,
  downloadUrl,
  downloadFileName,
  progress,
  duration,
  currentTime,
  onSeek,
  onForward,
  onRewind,
  isPdfLoaded,
  zoomLevel,
  onZoomIn,
  onZoomOut,
  isFullScreen,
  onFullScreenToggle,
  onSaveZoom,
  isSavingZoom,
}) => {
  const isGeneratingSpeech = docStatus === 'processing';
  const hasAudio = duration > 0;
  const isDesktop = useMediaQuery('(min-width: 768px)');

  const handleDownload = async () => {
    if (!showDownload || !downloadUrl) return;
    try {
      const response = await fetch(downloadUrl);
      if (!response.ok) throw new Error('Network response was not ok.');
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.style.display = 'none';
      a.href = url;
      a.download = downloadFileName;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      a.remove();
    } catch (error) {
      console.error('Download failed:', error);
      window.open(downloadUrl, '_blank');
    }
  };

  const getProcessingMessage = () => {
    switch(docStatus) {
        case 'processing': return 'Generating audio...';
        case 'failed': return 'Audio generation failed.';
        default: return '';
    }
  }


  return (
    <div className="p-2 md:p-4 w-full">
      <Card className="max-w-3xl mx-auto p-4 shadow-2xl bg-card/90 backdrop-blur-sm">
        <div className="flex items-center gap-2 md:gap-4">
            <div className="flex items-center gap-1 md:gap-2">
                <Tooltip>
                    <TooltipTrigger asChild>
                        <Button variant="ghost" size="icon" onClick={onRewind} disabled={!hasAudio}>
                            <Rewind className="h-5 w-5" />
                        </Button>
                    </TooltipTrigger>
                    <TooltipContent><p>Rewind 10s</p></TooltipContent>
                </Tooltip>
                <Button 
                    onClick={onPlayPause} 
                    size="lg" 
                    className="rounded-full w-14 h-14 bg-primary hover:bg-primary/90 text-primary-foreground"
                    aria-label={isSpeaking ? 'Pause' : 'Play'}
                    disabled={isGeneratingSpeech || !canPlay}
                >
                    {isGeneratingSpeech ? (
                        <Loader2 className="h-6 w-6 animate-spin" />
                    ) : (
                        isSpeaking ? <Pause className="h-6 w-6" /> : <Play className="h-6 w-6 ml-1" />
                    )}
                </Button>
                 <Tooltip>
                    <TooltipTrigger asChild>
                        <Button variant="ghost" size="icon" onClick={onForward} disabled={!hasAudio}>
                            <FastForward className="h-5 w-5" />
                        </Button>
                    </TooltipTrigger>
                    <TooltipContent><p>Forward 10s</p></TooltipContent>
                </Tooltip>
            </div>

            <div className="flex-1 flex flex-col gap-2">
                 <Slider
                    value={[currentTime]}
                    max={duration || 1}
                    step={1}
                    onValueChange={(value) => onSeek(value[0])}
                    disabled={!hasAudio}
                    className="w-full"
                />
                <div className="flex justify-between items-center text-xs text-muted-foreground">
                    <span>{formatTime(currentTime)}</span>
                    <span className={cn("text-xs font-medium flex items-center gap-1", docStatus === 'failed' && "text-destructive")}>
                      {docStatus === 'failed' && <AlertCircle className="h-4 w-4" />}
                      {getProcessingMessage()}
                    </span>
                    <span>{formatTime(duration)}</span>
                </div>
            </div>

            {isDesktop && <Separator orientation="vertical" className="h-10" />}

            <div className="hidden md:flex items-center gap-1">
                <Tooltip>
                    <TooltipTrigger asChild>
                        <Button variant="ghost" size="icon" onClick={onZoomOut} disabled={!isPdfLoaded || zoomLevel <= 0.4}>
                            <ZoomOut className="h-5 w-5" />
                        </Button>
                    </TooltipTrigger>
                    <TooltipContent><p>Zoom Out</p></TooltipContent>
                </Tooltip>
                <span className="text-sm font-semibold w-16 text-center">{(zoomLevel * 100).toFixed(0)}%</span>
                <Tooltip>
                    <TooltipTrigger asChild>
                        <Button variant="ghost" size="icon" onClick={onZoomIn} disabled={!isPdfLoaded || zoomLevel >= 3}>
                            <ZoomIn className="h-5 w-5" />
                        </Button>
                    </TooltipTrigger>
                    <TooltipContent><p>Zoom In</p></TooltipContent>
                </Tooltip>
                 <Tooltip>
                    <TooltipTrigger asChild>
                        <Button variant="ghost" size="icon" onClick={onSaveZoom} disabled={!isPdfLoaded || isSavingZoom}>
                            {isSavingZoom ? <Loader2 className="h-5 w-5 animate-spin" /> : <Save className="h-5 w-5" />}
                        </Button>
                    </TooltipTrigger>
                    <TooltipContent><p>Save Zoom Level</p></TooltipContent>
                </Tooltip>
                <Tooltip>
                    <TooltipTrigger asChild>
                        <Button variant="ghost" size="icon" onClick={onFullScreenToggle} disabled={!isPdfLoaded}>
                            {isFullScreen ? <Minimize className="h-5 w-5" /> : <Maximize className="h-5 w-5" />}
                        </Button>
                    </TooltipTrigger>
                    <TooltipContent><p>{isFullScreen ? 'Exit Fullscreen' : 'Fullscreen'}</p></TooltipContent>
                </Tooltip>
            </div>

            <Separator orientation="vertical" className="h-10" />

             <div className="flex items-center gap-1">
                <DropdownMenu>
                    <DropdownMenuTrigger asChild>
                    <Tooltip>
                        <TooltipTrigger asChild>
                            <Button variant="ghost" size={isDesktop ? 'sm' : 'icon'} className={isDesktop ? 'w-20' : ''} disabled={!hasAudio}>
                                <Wind className={isDesktop ? 'mr-2 h-4 w-4' : 'h-5 w-5'} />
                                {isDesktop && `${playbackRate.toFixed(2)}x`}
                            </Button>
                        </TooltipTrigger>
                        <TooltipContent><p>Playback Speed</p></TooltipContent>
                    </Tooltip>
                    </DropdownMenuTrigger>
                    <DropdownMenuContent align="center">
                    {playbackRates.map(rate => (
                        <DropdownMenuItem key={rate} onClick={() => onPlaybackRateChange(rate)}>
                            {rate.toFixed(2)}x {rate === 1.0 && "(Normal)"}
                        </DropdownMenuItem>
                    ))}
                    </DropdownMenuContent>
                </DropdownMenu>
                <Tooltip>
                    <TooltipTrigger asChild>
                        <Button variant="ghost" size="icon" disabled={!showDownload} onClick={handleDownload}>
                            <Download className="h-5 w-5"/>
                        </Button>
                    </TooltipTrigger>
                    <TooltipContent><p>Download Audio</p></TooltipContent>
                </Tooltip>
            </div>
        </div>
      </Card>
    </div>
  );
};

export default AudioPlayer;

src/components/audio-settings-panel.tsx.content:
'use client';

import React, { useMemo } from 'react';
import { Select, SelectContent, SelectGroup, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Label } from '@/components/ui/label';
import { Slider } from '@/components/ui/slider';
import { Button } from '@/components/ui/button';
import { Volume2 } from 'lucide-react';
import type { AvailableVoice } from '@/ai/flows/voice-selection';

type AudioSettingsPanelProps = {
  availableVoices: AvailableVoice[];
  selectedVoice: string;
  onSelectedVoiceChange: (voice: string) => void;
  speakingRate: number;
  onSpeakingRateChange: (rate: number) => void;
  isAudioGenerating: boolean;
  isSpeaking: boolean;
  onPreviewVoice: (voice: string) => void;
};

export default function AudioSettingsPanel({
  availableVoices,
  selectedVoice,
  onSelectedVoiceChange,
  speakingRate,
  onSpeakingRateChange,
  isAudioGenerating,
  isSpeaking,
  onPreviewVoice,
}: AudioSettingsPanelProps) {

  const groupedVoices = useMemo(() => {
    return availableVoices.reduce((acc, voice) => {
      const provider = voice.provider;
      if (!acc[provider]) {
        acc[provider] = [];
      }
      acc[provider].push(voice);
      return acc;
    }, {} as Record<string, AvailableVoice[]>);
  }, [availableVoices]);

  return (
    <div className="p-2 space-y-4">
      <div className='space-y-2'>
        <Label>Voice</Label>
        <Select value={selectedVoice} onValueChange={onSelectedVoiceChange} disabled={isSpeaking || isAudioGenerating}>
          <SelectTrigger>
            <SelectValue placeholder="Select a voice" />
          </SelectTrigger>
          <SelectContent>
            {Object.entries(groupedVoices).map(([provider, voices]) => (
              <SelectGroup key={provider}>
                <Label className="px-2 py-1.5 text-xs font-semibold text-muted-foreground">{provider.toUpperCase()}</Label>
                {voices.map((voice) => (
                  <div key={voice.name} className="flex items-center justify-between pr-2">
                    <SelectItem value={voice.name} className="flex-1">
                      {voice.displayName} ({voice.gender})
                    </SelectItem>
                    <Button
                      variant="ghost"
                      size="icon"
                      className="h-7 w-7 ml-2 shrink-0"
                      onClick={(e) => {
                        e.stopPropagation();
                        onPreviewVoice(voice.name);
                      }}
                      aria-label={`Preview voice ${voice.name}`}
                    >
                      <Volume2 className="h-4 w-4" />
                    </Button>
                  </div>
                ))}
              </SelectGroup>
            ))}
          </SelectContent>
        </Select>
      </div>
      <div className='space-y-2'>
        <Label htmlFor="speaking-rate">Speaking Rate: {speakingRate.toFixed(2)}x</Label>
        <Slider id="speaking-rate" min={0.25} max={4.0} step={0.25} value={[speakingRate]} onValueChange={(v) => onSpeakingRateChange(v[0])} disabled={isSpeaking || isAudioGenerating} />
      </div>
    </div>
  );
}

src/components/chat-window.tsx.content:
'use client';

import React, { useState, useRef, useEffect, forwardRef } from 'react';
import { Card, CardHeader, CardTitle, CardContent, CardFooter } from './ui/card';
import { Button } from './ui/button';
import { ScrollArea } from './ui/scroll-area';
import { Loader2, Minus, Send, X, Volume2, Trash2, Pause } from 'lucide-react';
import { type ChatMessage } from '@/lib/db';
import ReactMarkdown from 'react-markdown';
import { DraggableCore } from 'react-draggable';
import { Textarea } from './ui/textarea';
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog"

type ChatWindowProps = {
  chatHistory: ChatMessage[];
  isLoading: boolean;
  onSendMessage: (message: string) => void;
  onClose: () => void;
  onPlayAudio: (text: string) => void;
  onClearChat: () => void;
  isPlaying: boolean;
};

// Use forwardRef to get a ref to the underlying DOM element for react-draggable
export const ChatWindow = forwardRef<HTMLDivElement, ChatWindowProps>(
    ({ chatHistory, isLoading, onSendMessage, onClose, onPlayAudio, onClearChat, isPlaying }, ref) => {
  const [message, setMessage] = useState('');
  const [isMinimized, setIsMinimized] = useState(false);
  const scrollAreaRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (scrollAreaRef.current) {
        const scrollableView = scrollAreaRef.current.querySelector('div[data-radix-scroll-area-viewport]');
        if(scrollableView) {
            scrollableView.scrollTop = scrollableView.scrollHeight;
        }
    }
  }, [chatHistory]);

  const handleSubmit = (e?: React.FormEvent) => {
    e?.preventDefault();
    if (message.trim()) {
      onSendMessage(message);
      setMessage('');
    }
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLTextAreaElement>) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSubmit();
    }
  };

  if (isMinimized) {
    return (
      <div className="fixed bottom-4 right-4 z-50">
        <Button onClick={() => setIsMinimized(false)}>Chat ({chatHistory.length})</Button>
      </div>
    );
  }

  return (
    // Pass the ref to the div so DraggableCore can use it.
    <div ref={ref}>
        <DraggableCore 
            handle=".handle"
            nodeRef={ref as React.RefObject<HTMLDivElement>}
        >
          <div
            className="fixed bottom-4 right-4 z-50"
          >
            <Card className="w-[450px] h-[600px] flex flex-col shadow-2xl">
              <CardHeader className="handle cursor-move flex flex-row items-center justify-between p-4">
                <CardTitle>Chat with Document</CardTitle>
                <div className="flex items-center gap-1">
                    <AlertDialog>
                        <AlertDialogTrigger asChild>
                             <Button variant="ghost" size="icon" className="h-7 w-7"><Trash2 /></Button>
                        </AlertDialogTrigger>
                        <AlertDialogContent>
                            <AlertDialogHeader>
                            <AlertDialogTitle>Clear Chat History?</AlertDialogTitle>
                            <AlertDialogDescription>
                                This will permanently delete the chat history for this document. This action cannot be undone.
                            </AlertDialogDescription>
                            </AlertDialogHeader>
                            <AlertDialogFooter>
                            <AlertDialogCancel>Cancel</AlertDialogCancel>
                            <AlertDialogAction onClick={onClearChat}>Clear History</AlertDialogAction>
                            </AlertDialogFooter>
                        </AlertDialogContent>
                    </AlertDialog>
                    <Button variant="ghost" size="icon" className="h-7 w-7" onClick={() => setIsMinimized(true)}><Minus /></Button>
                    <Button variant="ghost" size="icon" className="h-7 w-7" onClick={onClose}><X /></Button>
                </div>
              </CardHeader>
              <CardContent className="flex-1 overflow-hidden p-0">
                <ScrollArea className="h-full p-4" ref={scrollAreaRef}>
                  <div className="space-y-4">
                    {chatHistory.map((chat) => (
                      <div key={chat.id} className={`flex ${chat.role === 'user' ? 'justify-end' : 'justify-start'}`}>
                        <div className={`max-w-[80%] p-3 rounded-lg ${chat.role === 'user' ? 'bg-primary text-primary-foreground' : 'bg-muted'}`}>
                           <div className="prose dark:prose-invert text-sm">
                             <ReactMarkdown>{chat.content}</ReactMarkdown>
                           </div>
                           {chat.role === 'assistant' && (
                               <Button variant="ghost" size="icon" className="h-6 w-6 mt-2" onClick={() => onPlayAudio(chat.content)}>
                                   {isPlaying ? <Pause className="h-4 w-4" /> : <Volume2 className="h-4 w-4" />}
                               </Button>
                           )}
                        </div>
                      </div>
                    ))}
                     {isLoading && (
                        <div className="flex justify-start">
                            <div className="bg-muted p-3 rounded-lg">
                                <Loader2 className="animate-spin" />
                            </div>
                        </div>
                    )}
                  </div>
                </ScrollArea>
              </CardContent>
              <CardFooter className="p-4">
                <form onSubmit={handleSubmit} className="flex w-full gap-2 items-start">
                  <Textarea
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    onKeyDown={handleKeyDown}
                    placeholder="Ask a question... (Shift + Enter for new line)"
                    disabled={isLoading}
                    className="min-h-[40px] max-h-48 resize-y"
                    rows={1}
                  />
                  <Button type="submit" disabled={isLoading || !message.trim()} className="self-end">
                    {isLoading ? <Loader2 className="animate-spin" /> : <Send />}
                  </Button>
                </form>
              </CardFooter>
            </Card>
          </div>
        </DraggableCore>
    </div>
  );
});
ChatWindow.displayName = "ChatWindow";

src/components/document-library.tsx.content:
'use client';

import React, { useState } from 'react';
import { Tooltip, TooltipContent, TooltipTrigger } from '@/components/ui/tooltip';
import { Button } from '@/components/ui/button';
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle, AlertDialogTrigger } from '@/components/ui/alert-dialog';
import { Mic, Trash2, FileText, Library, PlusCircle, Cloud, Folder as FolderIcon, FolderPlus, ChevronRight, UploadCloud, Loader2 } from 'lucide-react';
import { cn } from '@/lib/utils';
import type { Document, Folder } from '@/lib/db';
import { Input } from './ui/input';
import { useToast } from '@/hooks/use-toast';

type DocumentLibraryProps = {
  documents: Document[];
  folders: Folder[];
  activeDocId: string | null;
  onSelect: (doc: Document) => void;
  onDelete: (docId: string) => void;
  onGenerateAudio: (doc: Document) => void;
  onUploadNew: (folderId?: string) => void;
  onCreateFolder: (name: string) => Promise<void>;
  onDeleteFolder: (folderId: string) => Promise<void>;
  onMoveDocument: (docId: string, folderId: string | null) => Promise<void>;
};

export default function DocumentLibrary({
  documents,
  folders,
  activeDocId,
  onSelect,
  onDelete,
  onGenerateAudio,
  onUploadNew,
  onCreateFolder,
  onDeleteFolder,
  onMoveDocument,
}: DocumentLibraryProps) {
  const [expandedFolders, setExpandedFolders] = useState<Record<string, boolean>>({});
  const [isCreatingFolder, setIsCreatingFolder] = useState(false);
  const [newFolderName, setNewFolderName] = useState('');
  const [draggedDocId, setDraggedDocId] = useState<string | null>(null);
  const { toast } = useToast();

  const handleToggleFolder = (folderId: string) => {
    setExpandedFolders(prev => ({ ...prev, [folderId]: !prev[folderId] }));
  };

  const handleCreateFolder = async () => {
    if (!newFolderName.trim()) return;
    try {
        await onCreateFolder(newFolderName.trim());
        setNewFolderName('');
        setIsCreatingFolder(false);
    } catch(e) {
        toast({ variant: 'destructive', title: 'Error', description: 'Could not create folder.'})
    }
  };

  const handleDragStart = (e: React.DragEvent<HTMLDivElement>, docId: string) => {
    e.dataTransfer.effectAllowed = 'move';
    setDraggedDocId(docId);
  };

  const handleDrop = (e: React.DragEvent<HTMLDivElement>, folderId: string | null) => {
    e.preventDefault();
    if (draggedDocId) {
        onMoveDocument(draggedDocId, folderId);
        setDraggedDocId(null);
    }
  };

  const rootDocuments = documents.filter(d => !d.folderId);

  return (
    <div>
      <div className="flex justify-between items-center p-2 text-sm font-semibold text-muted-foreground">
        <div className="flex items-center gap-2">
          <Library />
          My Documents
        </div>
        <div className='flex items-center gap-1'>
            <Tooltip>
                <TooltipTrigger asChild>
                    <Button variant="ghost" size="icon" className="h-7 w-7" onClick={() => setIsCreatingFolder(true)}>
                        <FolderPlus className="h-5 w-5" />
                    </Button>
                </TooltipTrigger>
                <TooltipContent><p>New Folder</p></TooltipContent>
            </Tooltip>
            <Tooltip>
                <TooltipTrigger asChild>
                    <Button variant="ghost" size="icon" className="h-7 w-7" onClick={() => onUploadNew()}>
                        <PlusCircle className="h-5 w-5" />
                    </Button>
                </TooltipTrigger>
                <TooltipContent><p>New Document</p></TooltipContent>
            </Tooltip>
        </div>
      </div>
      <div className="px-2 space-y-1">
        {isCreatingFolder && (
            <div className='flex gap-2 p-1'>
                <Input 
                    value={newFolderName} 
                    onChange={e => setNewFolderName(e.target.value)}
                    placeholder="Folder Name"
                    className="h-8"
                    onKeyDown={e => e.key === 'Enter' && handleCreateFolder()}
                    autoFocus
                />
                <Button size="sm" onClick={handleCreateFolder}>Create</Button>
                <Button size="sm" variant="ghost" onClick={() => setIsCreatingFolder(false)}>Cancel</Button>
            </div>
        )}
        {folders.map(folder => (
            <div key={folder.id} onDragOver={e => e.preventDefault()} onDrop={(e) => handleDrop(e, folder.id)}>
                <div className="flex w-full items-center gap-2 overflow-hidden rounded-md p-2 text-left text-sm group bg-muted/50">
                    <button onClick={() => handleToggleFolder(folder.id)} className="flex items-center flex-1 gap-2">
                        <ChevronRight className={cn("h-4 w-4 transition-transform", expandedFolders[folder.id] && "rotate-90")}/>
                        <FolderIcon />
                        <span className="truncate max-w-[120px] font-medium">{folder.name}</span>
                    </button>
                    <div className="flex items-center ml-auto opacity-0 group-hover:opacity-100">
                        <Tooltip>
                            <TooltipTrigger asChild>
                                <Button variant="ghost" size="icon" className="h-7 w-7" onClick={() => onUploadNew(folder.id)}>
                                    <UploadCloud className="h-4 w-4" />
                                </Button>
                            </TooltipTrigger>
                            <TooltipContent><p>Upload to folder</p></TooltipContent>
                        </Tooltip>
                        <AlertDialog>
                            <Tooltip>
                                <TooltipTrigger asChild>
                                    <AlertDialogTrigger asChild>
                                        <Button variant="ghost" size="icon" className="h-7 w-7">
                                            <Trash2 className="h-4 w-4 text-destructive" />
                                        </Button>
                                    </AlertDialogTrigger>
                                </TooltipTrigger>
                                <TooltipContent><p>Delete folder</p></TooltipContent>
                            </Tooltip>
                            <AlertDialogContent>
                                <AlertDialogHeader>
                                <AlertDialogTitle>Delete "{folder.name}"?</AlertDialogTitle>
                                <AlertDialogDescription>
                                    This will permanently delete the folder and ALL documents inside it. This action cannot be undone.
                                </AlertDialogDescription>
                                </AlertDialogHeader>
                                <AlertDialogFooter>
                                <AlertDialogCancel>Cancel</AlertDialogCancel>
                                <AlertDialogAction onClick={() => onDeleteFolder(folder.id)} className="bg-destructive hover:bg-destructive/90">Delete</AlertDialogAction>
                                </AlertDialogFooter>
                            </AlertDialogContent>
                        </AlertDialog>
                    </div>
                </div>
                {expandedFolders[folder.id] && (
                    <div className="pl-6 pt-1 space-y-1">
                        {documents.filter(d => d.folderId === folder.id).map(doc => <DocumentItem key={doc.id} {...{doc, activeDocId, onSelect, onDelete, onGenerateAudio, onDragStart: handleDragStart}} />)}
                    </div>
                )}
            </div>
        ))}
         <div onDragOver={e => e.preventDefault()} onDrop={(e) => handleDrop(e, null)}>
            {rootDocuments.map(doc => <DocumentItem key={doc.id} {...{doc, activeDocId, onSelect, onDelete, onGenerateAudio, onDragStart: handleDragStart}} />)}
        </div>
      </div>
    </div>
  );
}

const DocumentItem = ({ doc, activeDocId, onSelect, onDelete, onGenerateAudio, onDragStart }: { doc: Document; activeDocId: string | null; onSelect: (doc: Document) => void; onDelete: (docId: string) => void; onGenerateAudio: (doc: Document) => void; onDragStart: (e: React.DragEvent<HTMLDivElement>, docId: string) => void; }) => (
    <div 
        draggable 
        onDragStart={(e) => onDragStart(e, doc.id)}
        className={cn(
            "flex w-full items-center gap-2 overflow-hidden rounded-md p-2 text-left text-sm group cursor-move",
            activeDocId === doc.id && "bg-sidebar-accent font-medium text-sidebar-accent-foreground"
    )}>
        <FileText />
        <div className="flex-1 flex items-center justify-between">
            <Tooltip>
            <TooltipTrigger asChild>
                <button onClick={() => onSelect(doc)} className="truncate max-w-[150px] text-left hover:underline">
                {doc.fileName}
                </button>
            </TooltipTrigger>
            <TooltipContent><p>{doc.fileName}</p></TooltipContent>
            </Tooltip>
            <div className="flex items-center opacity-0 group-hover:opacity-100 transition-opacity">
            {doc.audioGenerationStatus === 'processing' ? (
                <Tooltip>
                    <TooltipTrigger asChild><Loader2 className="h-4 w-4 animate-spin text-primary mr-1" /></TooltipTrigger>
                    <TooltipContent><p>Audio is generating...</p></TooltipContent>
                </Tooltip>
            ) : doc.audioGenerationStatus === 'completed' && doc.audioUrl ? (
                <Tooltip>
                    <TooltipTrigger asChild><Cloud className="h-4 w-4 text-primary mr-1" /></TooltipTrigger>
                    <TooltipContent><p>Audio is ready</p></TooltipContent>
                </Tooltip>
            ) : (
                <Tooltip>
                    <TooltipTrigger asChild>
                        <Button variant="ghost" size="icon" className="h-7 w-7" onClick={() => onGenerateAudio(doc)}>
                            <Mic className="h-4 w-4" />
                        </Button>
                    </TooltipTrigger>
                    <TooltipContent><p>Generate Audio</p></TooltipContent>
                </Tooltip>
            )}
            <AlertDialog>
                <Tooltip>
                <TooltipTrigger asChild>
                    <AlertDialogTrigger asChild>
                    <Button variant="ghost" size="icon" className="h-7 w-7">
                        <Trash2 className="h-4 w-4 text-destructive" />
                    </Button>
                    </AlertDialogTrigger>
                </TooltipTrigger>
                <TooltipContent><p>Delete document</p></TooltipContent>
                </Tooltip>
                <AlertDialogContent>
                <AlertDialogHeader>
                    <AlertDialogTitle>Are you sure?</AlertDialogTitle>
                    <AlertDialogDescription>
                    This will permanently delete "{doc.fileName}" and its generated audio. This action cannot be undone.
                    </AlertDialogDescription>
                </AlertDialogHeader>
                <AlertDialogFooter>
                    <AlertDialogCancel>Cancel</AlertDialogCancel>
                    <AlertDialogAction onClick={() => onDelete(doc.id)} className="bg-destructive hover:bg-destructive/90">Delete</AlertDialogAction>
                </AlertDialogFooter>
                </AlertDialogContent>
            </AlertDialog>
            </div>
        </div>
    </div>
);

src/components/impersonation-banner.tsx.content:
'use client';

import { useRouter } from 'next/navigation';
import { Button } from './ui/button';
import { UserX } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';

export default function ImpersonationBanner() {
  const router = useRouter();
  const { toast } = useToast();

  const handleStopImpersonating = async () => {
    const response = await fetch('/api/admin/stop-impersonating', { method: 'POST' });
    if (response.ok) {
      // Refresh the page to get the new session
      router.push('/admin');
      router.refresh();
    } else {
      toast({
        variant: 'destructive',
        title: 'Error',
        description: 'Could not stop impersonating. Please try logging out.',
      });
    }
  };

  return (
    <div className="bg-yellow-400 text-yellow-900 text-center p-2 flex items-center justify-center gap-4">
      <p className="font-semibold">You are currently impersonating a user.</p>
      <Button
        variant="secondary"
        size="sm"
        className="bg-yellow-500 hover:bg-yellow-600 text-yellow-900"
        onClick={handleStopImpersonating}
      >
        <UserX className="mr-2" />
        Return to Admin
      </Button>
    </div>
  );
}

src/components/main-content.tsx.content:
'use client';

import React from 'react';
import dynamic from 'next/dynamic';
import { Loader2, UploadCloud } from 'lucide-react';
import { Button } from '@/components/ui/button';
import type { Document } from '@/lib/db';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import TextToSpeechTab from './text-to-speech-tab';
import type { SpeechMark } from '@/hooks/use-read-page';

const PdfViewer = dynamic(() => import('@/components/pdf-viewer'), { 
  ssr: false,
  loading: () => (
    <div className="w-full h-full flex flex-col items-center justify-center text-center p-4 bg-muted/50 rounded-lg">
      <Loader2 className="h-12 w-12 animate-spin text-primary mb-4" />
      <p className="text-xl font-semibold">Loading Document Viewer...</p>
    </div>
  )
});

type MainContentProps = {
  activeDoc: Document | null;
  isUploading: boolean;
  uploadStage: 'idle' | 'uploading' | 'extracting' | 'cleaning' | 'saving' | 'error';
  pdfZoomLevel: number;
  onFileChange: (files: FileList | null) => void;
  fileInputRef: React.RefObject<HTMLInputElement>;
  onGenerateTextAudio: (text: string) => Promise<{ success: boolean; audioUrl?: string; error?: string }>;
  highlightedSentence: SpeechMark | null;
  highlightColor: string;
  highlightStyle: 'background' | 'underline';
};

export default function MainContent({
  activeDoc,
  isUploading,
  uploadStage,
  pdfZoomLevel,
  onFileChange,
  fileInputRef,
  onGenerateTextAudio,
  highlightedSentence,
  highlightColor,
  highlightStyle,
}: MainContentProps) {

  const getUploadMessage = () => {
    switch (uploadStage) {
      case 'uploading': return 'Uploading file...';
      case 'extracting': return 'Extracting text...';
      case 'cleaning': return 'Cleaning up content...';
      case 'saving': return 'Saving document...';
      case 'error': return 'An error occurred during upload.';
      default: return 'Drag & drop PDF here or click to upload';
    }
  };

  if (activeDoc) {
    return (
      <PdfViewer
        file={activeDoc.pdfUrl}
        zoomLevel={pdfZoomLevel}
        highlightedSentence={highlightedSentence}
        pageCharacterOffsets={activeDoc.pageCharacterOffsets}
        highlightColor={highlightColor}
        highlightStyle={highlightStyle}
        key={activeDoc.id} // Add key to force re-mount on doc change
      />
    );
  }

  if (isUploading) {
    return (
      <div className="w-full h-full flex flex-col items-center justify-center text-center p-4 bg-muted/50 rounded-lg">
        <Loader2 className="h-12 w-12 animate-spin text-primary mb-4" />
        <p className="text-xl font-semibold">{getUploadMessage()}</p>
        <p className="text-muted-foreground">Please wait while we process your document.</p>
      </div>
    );
  }

  return (
    <div
      className="w-full h-full flex items-center justify-center p-4"
      onDragOver={(e) => e.preventDefault()}
      onDrop={(e) => {
        e.preventDefault();
        onFileChange(e.dataTransfer.files);
      }}
    >
      <Tabs defaultValue="document" className="w-full max-w-2xl">
        <TabsList className="grid w-full grid-cols-2">
          <TabsTrigger value="document">Document Reader</TabsTrigger>
          <TabsTrigger value="tts">Text-to-Speech</TabsTrigger>
        </TabsList>
        <TabsContent value="document">
            <div className="text-center mt-6 p-8 border-2 border-dashed border-muted-foreground/30 rounded-xl">
                <UploadCloud className="mx-auto h-16 w-16 text-muted-foreground/50" />
                <h3 className="mt-4 text-2xl font-headline">Prepare a New Document</h3>
                <p className="mt-2 text-sm text-muted-foreground">
                Drag and drop a PDF file here, or click the button below to select one.
                </p>
                <Button className="mt-6" onClick={() => fileInputRef.current?.click()}>
                Select PDF File
                </Button>
                <input
                type="file"
                ref={fileInputRef}
                onChange={(e) => onFileChange(e.target.files)}
                accept="application/pdf"
                className="hidden"
                />
            </div>
        </TabsContent>
        <TabsContent value="tts">
            <TextToSpeechTab onGenerate={onGenerateTextAudio} />
        </TabsContent>
      </Tabs>
    </div>
  );
}

src/components/pdf-viewer.tsx.content:
'use client';

import React, { useState } from 'react';
import { Document, Page, pdfjs } from 'react-pdf';
import 'react-pdf/dist/esm/Page/AnnotationLayer.css';
import 'react-pdf/dist/esm/Page/TextLayer.css';
import { useToast } from '@/hooks/use-toast';
import { Loader2 } from 'lucide-react';

pdfjs.GlobalWorkerOptions.workerSrc = `/static/pdf.worker.min.js`;

type PdfViewerProps = {
  file: string;
  zoomLevel: number;
  highlightedSentence: any;
};

const PdfViewer: React.FC<PdfViewerProps> = ({ 
    file, 
    zoomLevel,
    highlightedSentence,
}) => {
  const [numPages, setNumPages] = useState<number | null>(null);
  const { toast } = useToast();

  const onDocumentLoadSuccess = ({ numPages }: { numPages: number }) => {
    setNumPages(numPages);
  };

  const onDocumentLoadError = (error: Error) => {
    if (error.name === 'AbortException') return;
    console.error("PDF Load Error:", error);
    toast({
      variant: 'destructive',
      title: 'Error loading PDF',
      description: error.message || 'Failed to load the document.',
    });
  };

  return (
    <div className="flex flex-col h-full w-full bg-muted">
      <div className="flex-1 overflow-auto relative flex items-start justify-center pt-4" id="pdf-viewer-container">
        <Document
          file={file}
          onLoadSuccess={onDocumentLoadSuccess}
          onLoadError={onDocumentLoadError}
          loading={
            <div className="flex items-center space-x-2 pt-20">
              <Loader2 className="animate-spin" />
              <span>Loading document...</span>
            </div>
          }
          className="flex flex-col items-center"
        >
          {numPages && Array.from(new Array(numPages), (el, index) => (
            <div 
              key={`page_container_${index + 1}`} 
              className="my-2 shadow-lg relative"
            >
              <Page
                pageNumber={index + 1}
                scale={zoomLevel}
                renderTextLayer={true}
                renderAnnotationLayer={true}
              />
            </div>
          ))}
        </Document>
      </div>
    </div>
  );
};

export default PdfViewer;

src/components/profile-dialog.tsx.content:
'use client';

import { useState, useEffect, useMemo } from 'react';
import { useForm, useFormState } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { Button } from '@/components/ui/button';
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Form, FormControl, FormDescription, FormField, FormItem, FormLabel, FormMessage } from '@/components/ui/form';
import { Input } from '@/components/ui/input';
import { useToast } from '@/hooks/use-toast';
import { changeUserPassword, updateUserProfile, deleteUserAccount } from '@/lib/user-actions';
import { Loader2, Trash2, Eye, EyeOff } from 'lucide-react';
import { useRouter } from 'next/navigation';
import { getUserSession, type Document, type UserSession } from '@/lib/db';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectGroup, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { getAvailableVoices, AvailableVoice } from '@/ai/flows/voice-selection';
import { Slider } from '@/components/ui/slider';
import { Tabs, TabsContent, TabsList, TabsTrigger } from './ui/tabs';
import { ScrollArea } from './ui/scroll-area';
import { RadioGroup, RadioGroupItem } from './ui/radio-group';
import { cn } from '@/lib/utils';

const passwordFormSchema = z.object({
  currentPassword: z.string().min(1, 'Current password is required.'),
  newPassword: z.string().min(8, 'New password must be at least 8 characters.'),
});
type PasswordFormValues = z.infer<typeof passwordFormSchema>;

const profileFormSchema = z.object({
  name: z.string().min(2, 'Name must be at least 2 characters.'),
  defaultVoice: z.string(),
  defaultSpeakingRate: z.number().min(0.25).max(4.0),
  defaultZoomLevel: z.number().min(0.4).max(3.0),
  highlightColor: z.string(),
  highlightStyle: z.enum(['background', 'underline']),
});
type ProfileFormValues = z.infer<typeof profileFormSchema>;

type ProfileDialogProps = {
  session: UserSession;
  onOpenChange: (open: boolean) => void;
  onUpdate: () => void; // To refresh session data on parent
};

const highlightColors = [
    { name: 'Yellow', value: 'highlight-yellow' },
    { name: 'Green', value: 'highlight-green' },
    { name: 'Blue', value: 'highlight-blue' },
    { name: 'Pink', value: 'highlight-pink' },
    { name: 'Purple', value: 'highlight-purple' },
];

export default function ProfileDialog({ session, onOpenChange, onUpdate }: ProfileDialogProps) {
  const { toast } = useToast();
  const router = useRouter();
  const [availableVoices, setAvailableVoices] = useState<AvailableVoice[]>([]);
  const [avatarPreview, setAvatarPreview] = useState<string | null>(null);
  const [isPasswordLoading, setIsPasswordLoading] = useState(false);
  const [isProfileLoading, setIsProfileLoading] = useState(false);
  const [isDeleteLoading, setIsDeleteLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [deleteConfirmation, setDeleteConfirmation] = useState('');
  const [isDeleteDialogOpen, setIsDeleteDialogOpen] = useState(false);

  useEffect(() => {
    async function fetchData() {
      const voices = await getAvailableVoices();
      setAvailableVoices(voices);

      if (session) {
        profileForm.reset({
            name: session.name || '',
            defaultVoice: session.defaultVoice || 'openai/alloy',
            defaultSpeakingRate: session.defaultSpeakingRate || 1.0,
            defaultZoomLevel: session.defaultZoomLevel || 1.0,
            highlightColor: session.highlightColor || 'highlight-yellow',
            highlightStyle: session.highlightStyle || 'background',
        });
      }
    }
    fetchData();
  }, [session]);

  const passwordForm = useForm<PasswordFormValues>({
    resolver: zodResolver(passwordFormSchema),
    defaultValues: { currentPassword: '', newPassword: '' },
  });

  const profileForm = useForm<ProfileFormValues>({
    resolver: zodResolver(profileFormSchema),
    defaultValues: {
      name: session.name || '',
      defaultVoice: session.defaultVoice || 'openai/alloy',
      defaultSpeakingRate: session.defaultSpeakingRate || 1.0,
      defaultZoomLevel: session.defaultZoomLevel || 1.0,
      highlightColor: session.highlightColor || 'highlight-yellow',
      highlightStyle: session.highlightStyle || 'background',
    }
  });

  const { isDirty: isProfileDirty } = useFormState({ control: profileForm.control });

  const onPasswordSubmit = async (values: PasswordFormValues) => {
    setIsPasswordLoading(true);
    try {
      const result = await changeUserPassword(values);
      if (result.success) {
        toast({ title: 'Success', description: 'Your password has been changed.' });
        passwordForm.reset();
      } else {
        throw new Error(result.message || 'Failed to change password.');
      }
    } catch (error) {
      toast({
        variant: 'destructive',
        title: 'Error',
        description: error instanceof Error ? error.message : 'An unexpected error occurred.',
      });
    } finally {
      setIsPasswordLoading(false);
    }
  };

  const onProfileSubmit = async (values: ProfileFormValues) => {
    setIsProfileLoading(true);
    const formData = new FormData();
    const avatarInput = document.querySelector('input[name="avatar"]') as HTMLInputElement;

    formData.append('name', values.name);
    formData.append('defaultVoice', values.defaultVoice);
    formData.append('defaultSpeakingRate', values.defaultSpeakingRate.toString());
    formData.append('defaultZoomLevel', values.defaultZoomLevel.toString());
    formData.append('highlightColor', values.highlightColor);
    formData.append('highlightStyle', values.highlightStyle);

    if (avatarInput?.files?.[0]) {
      formData.append('avatar', avatarInput.files[0]);
    }

    try {
      const result = await updateUserProfile(formData);
      if (result.success) {
        toast({ title: 'Success', description: 'Your profile has been updated.' });
        profileForm.reset(values); // Re-sync form state with new values
        onUpdate(); // Trigger parent to refetch session
      } else {
        throw new Error(result.message || 'Failed to update profile.');
      }
    } catch (error) {
      toast({
        variant: 'destructive',
        title: 'Error',
        description: error instanceof Error ? error.message : 'An unexpected error occurred.',
      });
    } finally {
      setIsProfileLoading(false);
    }
  };

  const handleDeleteAccount = async () => {
    if (deleteConfirmation !== session?.email) {
      toast({ variant: 'destructive', title: 'Confirmation failed', description: 'Please type your email correctly to confirm.' });
      return;
    }
    setIsDeleteLoading(true);
    try {
        const result = await deleteUserAccount(passwordForm.getValues('currentPassword'));
        if (result.success) {
            toast({ title: 'Account Deleted', description: 'Your account has been permanently deleted.' });
            router.push('/');
        } else {
            throw new Error(result.message || 'Failed to delete account.');
        }
    } catch (error) {
        toast({
            variant: 'destructive',
            title: 'Error',
            description: error instanceof Error ? error.message : 'An unexpected error occurred.',
        });
    } finally {
        setIsDeleteLoading(false);
        setIsDeleteDialogOpen(false);
        onOpenChange(false);
    }
  }

  const groupedVoices = useMemo(() => {
    return availableVoices.reduce((acc, voice) => {
        const provider = voice.provider;
        if (!acc[provider]) acc[provider] = [];
        acc[provider].push(voice);
        return acc;
    }, {} as Record<string, AvailableVoice[]>);
  }, [availableVoices]);

  if (!session) return null;

  return (
    <DialogContent className="max-w-2xl">
      <DialogHeader>
        <DialogTitle>My Account</DialogTitle>
        <DialogDescription>Manage your profile, preferences, and security settings.</DialogDescription>
      </DialogHeader>
      <Tabs defaultValue="profile">
        <TabsList className="grid w-full grid-cols-3">
          <TabsTrigger value="profile">Profile</TabsTrigger>
          <TabsTrigger value="security">Security</TabsTrigger>
          <TabsTrigger value="danger">Danger Zone</TabsTrigger>
        </TabsList>
        <TabsContent value="profile" className="py-4">
          <Form {...profileForm}>
            <form onSubmit={profileForm.handleSubmit(onProfileSubmit)}>
              <ScrollArea className="h-[60vh] pr-6">
                <div className="space-y-6">
                  <div className="flex items-center gap-6">
                    <Avatar className="h-20 w-20">
                      <AvatarImage src={avatarPreview || session.avatarUrl} data-ai-hint="user avatar" />
                      <AvatarFallback>{session.name?.charAt(0).toUpperCase()}</AvatarFallback>
                    </Avatar>
                    <FormField
                      control={profileForm.control}
                      name="name"
                      render={({ field }) => (
                      <FormItem className="flex-1">
                          <FormLabel>Display Name</FormLabel>
                          <FormControl><Input {...field} /></FormControl>
                          <FormMessage />
                      </FormItem>
                      )}
                    />
                  </div>
                  <FormItem>
                    <FormLabel>Profile Picture</FormLabel>
                    <FormControl>
                      <Input name="avatar" type="file" accept="image/*" onChange={(e) => {
                          if (e.target.files?.[0]) {
                              setAvatarPreview(URL.createObjectURL(e.target.files[0]));
                          }
                      }} />
                    </FormControl>
                    <FormDescription>Upload a new avatar. Max 2MB.</FormDescription>
                  </FormItem>

                  <h3 className="pt-4 border-t text-lg font-medium">Reading Preferences</h3>

                  <FormField
                    control={profileForm.control}
                    name="defaultVoice"
                    render={({ field }) => (
                    <FormItem>
                      <FormLabel>Default Voice</FormLabel>
                      <Select onValueChange={field.onChange} value={field.value}>
                          <FormControl><SelectTrigger><SelectValue/></SelectTrigger></FormControl>
                          <SelectContent>
                              {Object.entries(groupedVoices).map(([provider, voices]) => (
                                  <SelectGroup key={provider}>
                                      <Label className="px-2 py-1.5 text-xs font-semibold text-muted-foreground">{provider.toUpperCase()}</Label>
                                      {voices.map(v => <SelectItem key={v.name} value={v.name}>{v.displayName} ({v.gender})</SelectItem>)}
                                  </SelectGroup>
                              ))}
                          </SelectContent>
                      </Select>
                      <FormDescription>Your default voice for new documents.</FormDescription>
                      <FormMessage />
                    </FormItem>
                    )}
                  />
                  <FormField
                    control={profileForm.control}
                    name="defaultSpeakingRate"
                    render={({ field }) => (
                    <FormItem>
                      <FormLabel>Default Speaking Rate: {field.value?.toFixed(2)}x</FormLabel>
                      <FormControl>
                          <Slider min={0.25} max={4.0} step={0.25} value={[field.value]} onValueChange={(v) => field.onChange(v[0])} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                    )}
                  />
                  <FormField
                    control={profileForm.control}
                    name="defaultZoomLevel"
                    render={({ field }) => (
                    <FormItem>
                      <FormLabel>Default PDF Zoom: {(field.value * 100).toFixed(0)}%</FormLabel>
                      <FormControl>
                          <Slider min={0.4} max={3.0} step={0.1} value={[field.value]} onValueChange={(v) => field.onChange(v[0])} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                    )}
                  />

                <FormField
                    control={profileForm.control}
                    name="highlightColor"
                    render={({ field }) => (
                        <FormItem>
                            <FormLabel>Highlight Color</FormLabel>
                            <FormControl>
                                <RadioGroup onValueChange={field.onChange} value={field.value} className="flex flex-wrap gap-4">
                                    {highlightColors.map(color => (
                                        <FormItem key={color.value} className="flex items-center space-x-2">
                                            <FormControl>
                                                <RadioGroupItem value={color.value} id={color.value} />
                                            </FormControl>
                                            <Label htmlFor={color.value} className="flex items-center gap-2 font-normal">
                                                <span className={cn("w-4 h-4 rounded-full border", `bg-${color.value}`)}></span>
                                                {color.name}
                                            </Label>
                                        </FormItem>
                                    ))}
                                </RadioGroup>
                            </FormControl>
                        </FormItem>
                    )}
                />
                <FormField
                    control={profileForm.control}
                    name="highlightStyle"
                    render={({ field }) => (
                        <FormItem>
                            <FormLabel>Highlight Style</FormLabel>
                             <FormControl>
                                <RadioGroup onValueChange={field.onChange} value={field.value} className="flex gap-4">
                                     <FormItem className="flex items-center space-x-2">
                                        <FormControl><RadioGroupItem value="background" id="style-bg" /></FormControl>
                                        <Label htmlFor="style-bg" className="font-normal">Background</Label>
                                    </FormItem>
                                    <FormItem className="flex items-center space-x-2">
                                        <FormControl><RadioGroupItem value="underline" id="style-ul" /></FormControl>
                                        <Label htmlFor="style-ul" className="font-normal">Underline</Label>
                                    </FormItem>
                                </RadioGroup>
                            </FormControl>
                        </FormItem>
                    )}
                />

                </div>
              </ScrollArea>
              <DialogFooter className="pt-6">
                <Button type="submit" disabled={isProfileLoading || !isProfileDirty}>
                    {isProfileLoading && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
                    Save Changes
                </Button>
              </DialogFooter>
            </form>
          </Form>
        </TabsContent>
        <TabsContent value="security" className="py-4">
             <Form {...passwordForm}>
                <form onSubmit={passwordForm.handleSubmit(onPasswordSubmit)} className="space-y-6">
                    <FormField control={passwordForm.control} name="currentPassword" render={({ field }) => (
                        <FormItem><FormLabel>Current Password</FormLabel><FormControl><Input type="password" {...field} /></FormControl><FormMessage /></FormItem>
                    )} />
                    <FormField control={passwordForm.control} name="newPassword" render={({ field }) => (
                        <FormItem><FormLabel>New Password</FormLabel><FormControl><Input type="password" {...field} /></FormControl><FormMessage /></FormItem>
                    )} />
                    <DialogFooter>
                      <Button type="submit" disabled={isPasswordLoading}>
                          {isPasswordLoading && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
                          Update Password
                      </Button>
                    </DialogFooter>
                </form>
            </Form>
        </TabsContent>
        <TabsContent value="danger" className="py-4">
            <p className="text-sm text-destructive mb-4">This action is permanent and cannot be undone.</p>
            <Dialog open={isDeleteDialogOpen} onOpenChange={setIsDeleteDialogOpen}>
                <DialogTrigger asChild>
                    <Button variant="destructive"><Trash2 className="mr-2"/>Delete My Account</Button>
                </DialogTrigger>
                <DialogContent>
                    <DialogHeader>
                        <DialogTitle>Are you absolutely sure?</DialogTitle>
                        <DialogDescription>
                            This will permanently delete your account and all associated data. To confirm, please type your email address (`{session.email}`) and current password below.
                        </DialogDescription>
                    </DialogHeader>
                    <div className="space-y-4 py-4">
                         <Input 
                            placeholder="Enter your email to confirm"
                            value={deleteConfirmation}
                            onChange={(e) => setDeleteConfirmation(e.target.value)}
                        />
                        <div className="space-y-2 relative">
                            <Label htmlFor="delete-password">Enter Your Current Password</Label>
                            <Input {...passwordForm.register("currentPassword")} id="delete-password" type={showPassword ? 'text' : 'password'} required />
                            <Button type="button" variant="ghost" size="icon" className="absolute bottom-1 right-1 h-7 w-7" onClick={() => setShowPassword(!showPassword)}>
                            {showPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                            </Button>
                        </div>
                    </div>
                    <DialogFooter>
                        <Button variant="ghost" onClick={() => setIsDeleteDialogOpen(false)}>Cancel</Button>
                        <Button variant="destructive" onClick={handleDeleteAccount} disabled={isDeleteLoading || deleteConfirmation !== session.email}>
                            {isDeleteLoading && <Loader2 className="mr-2 h-4 w-4 animate-spin"/>}
                            I understand, delete my account
                        </Button>
                    </DialogFooter>
                </DialogContent>
            </Dialog>
        </TabsContent>
      </Tabs>
    </DialogContent>
  );
}

src/components/text-to-speech-tab.tsx.content:
'use client';

import { useState } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Textarea } from '@/components/ui/textarea';
import { Button } from '@/components/ui/button';
import { Download, Loader2, Mic } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';

type TextToSpeechTabProps = {
  onGenerate: (text: string) => Promise<{ success: boolean; audioUrl?: string; error?: string }>;
};

export default function TextToSpeechTab({ onGenerate }: TextToSpeechTabProps) {
  const [text, setText] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [audioUrl, setAudioUrl] = useState<string | null>(null);
  const { toast } = useToast();

  const handleGenerate = async () => {
    if (!text.trim()) {
      toast({ variant: 'destructive', title: 'Error', description: 'Please enter some text to generate audio.' });
      return;
    }
    setIsLoading(true);
    setAudioUrl(null);
    try {
      const result = await onGenerate(text);
      if (result.success && result.audioUrl) {
        setAudioUrl(result.audioUrl);
        toast({ title: 'Success', description: 'Audio generated and will play automatically.' });
      } else {
        throw new Error(result.error || 'Failed to generate audio.');
      }
    } catch (error) {
      const message = error instanceof Error ? error.message : 'An unknown error occurred.';
      toast({ variant: 'destructive', title: 'Error', description: message });
    } finally {
      setIsLoading(false);
    }
  };

  const handleDownload = () => {
    if (!audioUrl) return;
    const a = document.createElement('a');
    a.href = audioUrl;
    a.download = 'speech.mp3';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  return (
    <Card className="mt-6 border-0 shadow-none">
      <CardHeader className="text-center px-0">
        <CardTitle className="text-2xl font-headline">Freestyle Text-to-Speech</CardTitle>
        <CardDescription>Type or paste any text below to convert it into audio.</CardDescription>
      </CardHeader>
      <CardContent className="px-0">
        <div className="grid w-full gap-4">
          <Textarea
            placeholder="Type your text here..."
            rows={10}
            value={text}
            onChange={(e) => setText(e.target.value)}
            disabled={isLoading}
          />
          <div className="flex gap-2 justify-center">
            <Button onClick={handleGenerate} disabled={isLoading || !text.trim()} className="w-48">
              {isLoading ? <Loader2 className="animate-spin" /> : <Mic />}
              {isLoading ? 'Generating...' : 'Generate Audio'}
            </Button>
            <Button
              onClick={handleDownload}
              disabled={!audioUrl || isLoading}
              variant="outline"
              className="w-48"
            >
              <Download />
              Download Audio
            </Button>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

