'use server';

import { kv } from '@vercel/kv';
import type { VoiceProviderSettings } from './admin-actions';

const CLEANUP_FLAG_KEY = 'readify:cleanup:lemonfox-setting-removed';

/**
 * A one-time action to remove the 'lemonfox' provider from saved voice settings in KV.
 * It uses a flag to ensure it only runs once per environment.
 */
export async function removeLemonfoxFromSettings(): Promise<{ success: boolean; message: string }> {
  try {
    const hasRun = await kv.get(CLEANUP_FLAG_KEY);

    if (hasRun) {
      return { success: true, message: 'Cleanup has already been performed.' };
    }

    console.log('Running one-time cleanup to remove Lemonfox from voice settings...');

    const VOICE_SETTINGS_KEY = 'readify:settings:voice-providers';
    const settings = await kv.get<VoiceProviderSettings>(VOICE_SETTINGS_KEY);

    if (settings && 'lemonfox' in settings) {
      delete settings.lemonfox;
      await kv.set(VOICE_SETTINGS_KEY, settings);
      console.log('Successfully removed Lemonfox key from voice settings.');
    } else {
      console.log('No Lemonfox key found in voice settings. No action needed.');
    }

    // Set the flag to prevent this from running again.
    await kv.set(CLEANUP_FLAG_KEY, true);

    return { success: true, message: 'Cleanup completed successfully.' };
  } catch (error) {
    const message = error instanceof Error ? error.message : 'An unknown error occurred.';
    console.error('Error during settings cleanup:', message);
    return { success: false, message };
  }
}

src/lib/db.ts.content:
'use server';

import { kv } from '@vercel/kv';
import { del as deleteBlob } from '@vercel/blob';
import { getSession, type SessionPayload } from './session';
import { randomUUID } from 'crypto';
import { s3Client } from '@/ai/flows/speech-generation/amazon';
import { DeleteObjectCommand } from '@aws-sdk/client-s3';

export interface ChatMessage {
  id: string;
  role: 'user' | 'assistant';
  content: string;
  createdAt: string;
}

export interface QuizAttempt {
    questions: any[]; // Store the questions for review
    answers: Record<number, string>;
    score: number;
    suggestions: string;
    completedAt: string;
}

export interface Folder {
    id: string;
    userId: string;
    name: string;
    createdAt: string;
}

export type AudioGenerationStatus = 'idle' | 'processing' | 'completed' | 'failed';

export interface Document {
  id: string; 
  userId: string;
  fileName: string;
  pdfUrl: string;
  textContent: string;
  audioUrl: string | null;
  speechMarksUrl: string | null;
  unwantedText: string[] | null;
  audioGenerationStatus: AudioGenerationStatus;
  audioGenerationError?: string | null;
  zoomLevel: number;
  createdAt: string;
  folderId: string | null;
  chatHistory?: ChatMessage[];
  quizAttempt?: QuizAttempt | null;
}

export interface User {
    id: string;
    name: string;
    email: string;
    username: string | null;
    password: string; 
    isAdmin: boolean;
    createdAt: string;
    setupToken: string | null;
    setupTokenExpiry: string | null;
    avatarUrl?: string | null;
    defaultVoice?: string | null;
    defaultSpeakingRate?: number | null;
    defaultZoomLevel?: number | null;
}

export interface UserSession extends SessionPayload {
    name: string;
    email: string;
    avatarUrl?: string | null;
    defaultVoice?: string | null;
    defaultSpeakingRate?: number | null;
    defaultZoomLevel?: number | null;
}

export interface Submission {
    id: string;
    type: 'Access Request' | 'General Inquiry';
    name: string;
    email: string;
    message: string;
    status: 'Pending' | 'Approved' | 'Rejected' | 'Replied';
    createdAt: string;
}


export async function getUserSession(): Promise<UserSession | null> {
  const session = await getSession();
  if (session?.userId) {
    const user: User | null = await kv.get(`readify:user:id:${session.userId}`);
    if (user) {
        return {
            ...session,
            name: user.name,
            email: user.email,
            avatarUrl: user.avatarUrl,
            defaultVoice: user.defaultVoice,
            defaultSpeakingRate: user.defaultSpeakingRate,
            defaultZoomLevel: user.defaultZoomLevel,
        };
    }
  }
  return null;
}

export async function saveDocument(docData: Partial<Document>): Promise<Document> {
  const session = await getSession();
  if (!session?.userId) {
    throw new Error('Authentication required.');
  }
  const userId = session.userId;
  const prefix = 'readify';

  let docId = docData.id;
  const userDocListKey = `${prefix}:user:${userId}:docs`;

  if (docId) {
    const docKey = `${prefix}:doc:${docId}`;
    const existingDocRaw: Document | null = await kv.get(docKey);
    if (!existingDocRaw) {
        throw new Error('Document not found.');
    }

    if (!session.isAdmin && existingDocRaw.userId !== userId) {
      throw new Error('Access denied.');
    }
    const updatedDoc: Document = {
      ...existingDocRaw,
      ...docData,
      id: docId,
    };
    await kv.set(docKey, updatedDoc);
    return updatedDoc;

  } else {
    if (!docData.fileName || !docData.pdfUrl || !docData.textContent) {
      throw new Error("fileName, pdfUrl, and textContent are required for new documents.");
    }
    docId = randomUUID();
    const newDoc: Document = {
      id: docId,
      userId,
      fileName: docData.fileName,
      pdfUrl: docData.pdfUrl,
      textContent: docData.textContent,
      audioUrl: docData.audioUrl || null,
      speechMarksUrl: null,
      unwantedText: null,
      audioGenerationStatus: 'idle',
      zoomLevel: docData.zoomLevel || 1,
      createdAt: new Date().toISOString(),
      folderId: docData.folderId || null,
      chatHistory: [],
    };

    const docKey = `${prefix}:doc:${docId}`;
    const pipeline = kv.pipeline();
    pipeline.set(docKey, newDoc);
    pipeline.lpush(userDocListKey, docId);
    await pipeline.exec();

    return newDoc;
  }
}

export async function getDocuments(): Promise<Document[]> {
  const session = await getSession();
  if (!session?.userId) {
    return [];
  }
  const userId = session.userId;
  const prefix = 'readify';
  const userDocListKey = `${prefix}:user:${userId}:docs`;

  const docIds = await kv.lrange<string[]>(userDocListKey, 0, -1);
  if (docIds.length === 0) {
    return [];
  }

  const validDocIds = docIds.filter(id => id);
  if (validDocIds.length === 0) {
    return [];
  }

  const docKeys = validDocIds.map(id => `${prefix}:doc:${id}`);
  const docs = await kv.mget<Document[]>(...docKeys);

  return docs
    .filter((doc): doc is Document => doc !== null)
    .sort((a,b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime());
}

async function deleteS3Object(url: string) {
    try {
        const urlObj = new URL(url);
        // Extract bucket and key. Assumes standard S3 URL format.
        const bucketName = urlObj.hostname.split('.')[0];
        const objectKey = urlObj.pathname.substring(1); 

        if (!bucketName || !objectKey) {
            throw new Error("Could not parse S3 URL for deletion.");
        }

        const command = new DeleteObjectCommand({
            Bucket: bucketName,
            Key: decodeURIComponent(objectKey),
        });

        await s3Client.send(command);
        console.log(`Successfully deleted ${objectKey} from S3 bucket ${bucketName}.`);

    } catch (s3Error) {
        // Log error but don't re-throw, as the object might have already been deleted
        // or the URL might not have been an S3 URL.
        console.error("Failed to delete object from S3, it may have already been removed:", s3Error);
    }
}

export async function deleteDocument(docId: string): Promise<{ success: boolean, message?: string }> {
    const session = await getSession();
    if (!session?.userId) {
        throw new Error('Authentication required.');
    }
    const prefix = 'readify';

    try {
        const docKey = `${prefix}:doc:${docId}`;
        const doc: Document | null = await kv.get(docKey);

        if (!doc) {
            return { success: true, message: 'Document already deleted.' };
        }

        if (doc.userId !== session.userId && !session.isAdmin) {
            throw new Error('You do not have permission to delete this document.');
        }

        // Delete Vercel Blob assets
        if (doc.pdfUrl && doc.pdfUrl.includes('.blob.vercel-storage.com')) {
            await deleteBlob(doc.pdfUrl);
        }
        if (doc.audioUrl && doc.audioUrl.includes('.blob.vercel-storage.com')) {
            await deleteBlob(doc.audioUrl);
        }

        // Delete S3 assets
        if (doc.audioUrl && doc.audioUrl.includes('s3.amazonaws.com')) {
            await deleteS3Object(doc.audioUrl);
        }
         if (doc.speechMarksUrl && doc.speechMarksUrl.includes('s3.amazonaws.com')) {
            await deleteS3Object(doc.speechMarksUrl);
        }

        const pipeline = kv.pipeline();
        pipeline.del(docKey);
        pipeline.lrem(`${prefix}:user:${doc.userId}:docs`, 1, docId);
        await pipeline.exec();

        return { success: true };
    } catch (error) {
        const message = error instanceof Error ? error.message : 'An unknown error occurred';
        console.error('Failed to delete document:', message);
        return { success: false, message };
    }
}

export async function clearChatHistory(docId: string): Promise<Document> {
    const session = await getSession();
    if (!session?.userId) {
        throw new Error('Authentication required.');
    }
    const prefix = 'readify';
    const docKey = `${prefix}:doc:${docId}`;
    const doc: Document | null = await kv.get(docKey);

    if (!doc) {
        throw new Error('Document not found.');
    }

    if (doc.userId !== session.userId && !session.isAdmin) {
        throw new Error('You do not have permission to modify this document.');
    }

    const updatedDoc: Document = {
        ...doc,
        chatHistory: [],
    };

    await kv.set(docKey, updatedDoc);

    return updatedDoc;
}

export async function getFolders(): Promise<Folder[]> {
    const session = await getSession();
    if (!session?.userId) return [];

    const folderListKey = `readify:user:${session.userId}:folders`;
    const folderIds = await kv.lrange<string[]>(folderListKey, 0, -1);
    if (folderIds.length === 0) return [];

    const validFolderIds = folderIds.filter(id => id);
    if (validFolderIds.length === 0) return [];

    const folderKeys = validFolderIds.map(id => `readify:folder:${id}`);
    const folders = await kv.mget<Folder[]>(...folderKeys);

    return folders
        .filter((f): f is Folder => f !== null)
        .sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime());
}

export async function createFolder(name: string): Promise<Folder> {
    const session = await getSession();
    if (!session?.userId) throw new Error("Authentication required.");

    const newFolder: Folder = {
        id: randomUUID(),
        userId: session.userId,
        name,
        createdAt: new Date().toISOString()
    };

    const pipeline = kv.pipeline();
    pipeline.set(`readify:folder:${newFolder.id}`, newFolder);
    pipeline.lpush(`readify:user:${session.userId}:folders`, newFolder.id);
    await pipeline.exec();

    return newFolder;
}

export async function deleteFolder(folderId: string): Promise<{ success: boolean, message?: string }> {
    const session = await getSession();
    if (!session?.userId) throw new Error("Authentication required.");

    const folder: Folder | null = await kv.get(`readify:folder:${folderId}`);
    if (!folder || folder.userId !== session.userId) {
        throw new Error("Folder not found or access denied.");
    }

    // Get all document IDs for the user
    const userDocListKey = `readify:user:${session.userId}:docs`;
    const docIds = await kv.lrange<string[]>(userDocListKey, 0, -1);
    if (docIds.length > 0) {
        const docKeys = docIds.map(id => `readify:doc:${id}`);
        const docs = await kv.mget<Document[]>(...docKeys);
        const docsToDelete = docs.filter((doc): doc is Document => doc !== null && doc.folderId === folderId);

        // Delete each document inside the folder
        for (const doc of docsToDelete) {
            await deleteDocument(doc.id);
        }
    }

    // Delete the folder itself
    const pipeline = kv.pipeline();
    pipeline.del(`readify:folder:${folderId}`);
    pipeline.lrem(`readify:user:${session.userId}:folders`, 1, folderId);
    await pipeline.exec();

    return { success: true };
}

export async function moveDocumentToFolder(docId: string, folderId: string | null): Promise<Document> {
    const session = await getSession();
    if (!session?.userId) throw new Error("Authentication required.");

    const doc: Document | null = await kv.get(`readify:doc:${docId}`);
    if (!doc || doc.userId !== session.userId) {
        throw new Error("Document not found or access denied.");
    }

    if (folderId) {
        const folder: Folder | null = await kv.get(`readify:folder:${folderId}`);
        if (!folder || folder.userId !== session.userId) {
            throw new Error("Target folder not found or access denied.");
        }
    }

    const updatedDoc = { ...doc, folderId };
    await kv.set(`readify:doc:${doc.id}`, updatedDoc);
    return updatedDoc;
}

src/lib/email.ts.content:
'use server';

import { Resend } from 'resend';
import { WelcomeEmail } from '@/components/emails/welcome-email';
import { ContactFormEmail } from '@/components/emails/contact-form-email';
import { RejectionEmail } from '@/components/emails/rejection-email';
import { GeneralContactFormEmail } from '@/components/emails/general-contact-form-email';
import { AdminReplyEmail } from '@/components/emails/admin-reply-email';


const resend = new Resend(process.env.RESEND_API_KEY);
const fromEmail = process.env.FROM_EMAIL || 'onboarding@resend.dev';
const toEmail = process.env.ADMIN_EMAIL || process.env.FROM_EMAIL || 'onboarding@resend.dev';

export async function sendWelcomeEmail(
  to: string,
  name: string,
  setupLink: string
) {
  try {
    const { data, error } = await resend.emails.send({
      from: `Readify <${fromEmail}>`,
      to,
      subject: 'Welcome to Readify! Complete Your Account Setup',
      react: WelcomeEmail({ name, setupLink }),
    });

    if (error) {
      console.error('Resend Error:', error);
      // Throw a more specific error to be caught by the calling action
      throw new Error(`Resend failed: ${error.message}`);
    }

    console.log('Welcome email sent successfully:', data?.id);
    return data;
  } catch (error) {
    // Catch the error and re-throw it to ensure the calling function knows about it.
    // This now includes our more specific error from above.
    console.error('Error in sendWelcomeEmail:', error);
    const message = error instanceof Error ? error.message : JSON.stringify(error);
    throw new Error(`Failed to send welcome email: ${message}`);
  }
}

export async function sendContactFormEmail({ name, email, message, approveUrl, rejectUrl }: { name: string, email: string, message: string, approveUrl: string, rejectUrl: string }) {
    try {
        const { data, error } = await resend.emails.send({
            from: `Readify Access Request <${fromEmail}>`,
            to: toEmail,
            subject: `New Readify Access Request from ${name}`,
