    />
    </TooltipProvider>
  );
}

export default function AdminPage() {
  return (
    <Suspense fallback={
        <div className="flex h-screen w-full items-center justify-center">
            <Loader2 className="h-8 w-8 animate-spin text-primary" />
        </div>
    }>
      <AdminDashboard />
    </Suspense>
  );
}

