import { NextRequest, NextResponse } from 'next/server';
import { put } from '@vercel/blob';
import { getSession } from '@/lib/session';
import { randomUUID } from 'crypto';

export async function POST(request: NextRequest) {
  const session = await getSession();
  // Allow uploads even if username is not set yet (e.g., during initial document processing)
  if (!session?.userId) {
    return NextResponse.json({ message: 'Unauthorized' }, { status: 401 });
  }

  const originalFilename = request.headers.get('x-vercel-filename');
  if (!originalFilename) {
    return NextResponse.json(
      { message: 'Filename is missing' },
      { status: 400 }
    );
  }

  if (!request.body) {
    return NextResponse.json(
      { message: 'Request body is missing' },
      { status: 400 }
    );
  }

  // Use a unique identifier for the user folder, which is the userId.
  const userIdentifier = session.userId;
  const docId = request.headers.get('x-doc-id') || randomUUID();
  const blobName = `readify/${userIdentifier}/${docId}-${originalFilename}`;

  try {
    const blob = await put(blobName, request.body, {
      access: 'public',
      addRandomSuffix: false,
    });

    return NextResponse.json(blob);

  } catch (error) {
    console.error('Error uploading file:', error);
    return NextResponse.json(
      { message: 'Error uploading file' },
      { status: 500 }
    );
  }
}

src/app/setup-account/[token]/page.tsx.content:
'use client';

import { useState } from 'react';
import { useRouter } from 'next/navigation';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from '@/components/ui/form';
import { Input } from '@/components/ui/input';
import { useToast } from '@/hooks/use-toast';
import { setupAccount } from '@/lib/actions';
import { Loader2 } from 'lucide-react';

const formSchema = z.object({
  username: z.string()
    .min(3, 'Username must be at least 3 characters.')
    .max(20, 'Username cannot be longer than 20 characters.')
    .regex(/^[a-z0-9_.]+$/, 'Username can only contain lowercase letters, numbers, underscores, and periods.'),
  password: z.string().min(8, 'New password must be at least 8 characters.'),
});

type SetupFormValues = z.infer<typeof formSchema>;

interface SetupAccountPageProps {
  params: {
    token:string;
  };
  searchParams?: { [key: string]: string | string[] | undefined };
}

export default function SetupAccountPage({ params }: SetupAccountPageProps) {
  const router = useRouter();
  const { toast } = useToast();
  const [isLoading, setIsLoading] = useState(false);

  const form = useForm<SetupFormValues>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      username: '',
      password: '',
    },
  });

  const onSubmit = async (values: SetupFormValues) => {
    setIsLoading(true);
    try {
      const result = await setupAccount({ ...values, token: params.token });
      if (result.success) {
        toast({
          title: 'Account Setup Complete!',
          description: 'You can now log in with your new credentials.',
        });
        router.push('/read');
        router.refresh();
      } else {
        throw new Error(result.message || 'Failed to set up account.');
      }
    } catch (error) {
      toast({
        variant: 'destructive',
        title: 'Error',
        description: error instanceof Error ? error.message : 'An unexpected error occurred.',
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <main className="flex items-center justify-center min-h-screen bg-background p-4">
      <Card className="w-full max-w-md shadow-2xl">
        <CardHeader>
          <CardTitle>Set Up Your Readify Account</CardTitle>
          <CardDescription>
            Choose a unique username and a secure password to complete your registration.
          </CardDescription>
        </CardHeader>
        <CardContent>
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
              <FormField
                control={form.control}
                name="username"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Username</FormLabel>
                    <FormControl>
                      <Input placeholder="your.username" {...field} />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <FormField
                control={form.control}
                name="password"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Password</FormLabel>
                    <FormControl>
                      <Input type="password" {...field} />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <div className="pt-2">
                <Button type="submit" className="w-full" disabled={isLoading}>
                  {isLoading && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
                  Complete Setup
                </Button>
              </div>
            </form>
          </Form>
        </CardContent>
      </Card>
    </main>
  );
}

src/app/api/admin/impersonate/route.ts.content:
import { NextRequest, NextResponse } from 'next/server';
import { getSession, createSession } from '@/lib/session';
import { kv } from '@vercel/kv';
import type { User } from '@/lib/db';

export async function POST(req: NextRequest) {
  const adminSession = await getSession();

  // 1. Check if the current user is an admin
  if (!adminSession?.isAdmin) {
    return NextResponse.json({ message: 'Unauthorized' }, { status: 403 });
  }

  const { userId } = await req.json();

  if (!userId) {
    return NextResponse.json({ message: 'User ID is required' }, { status: 400 });
  }

  // 2. Find the user to impersonate
  const userToImpersonate: User | null = await kv.get(`readify:user:id:${userId}`);

  if (!userToImpersonate) {
    return NextResponse.json({ message: 'User not found' }, { status: 404 });
  }

  if (userToImpersonate.isAdmin) {
      return NextResponse.json({ message: 'Cannot impersonate another admin.' }, { status: 403 });
  }

  // 3. Store the original admin's ID
  const adminId = adminSession.userId;
  const response = NextResponse.json({ success: true });
  response.cookies.set('impersonator_id', adminId, {
    httpOnly: true,
    secure: process.env.NODE_ENV === 'production',
    sameSite: 'strict',
    path: '/',
  });

  // 4. Create a new session for the impersonated user
  // This will overwrite the current 'session' cookie
  await createSession(userToImpersonate.id, userToImpersonate.isAdmin, userToImpersonate.username, response.cookies);

  return response;
}

src/app/api/admin/stop-impersonating/rou.content:
import { NextRequest, NextResponse } from 'next/server';
import { createSession } from '@/lib/session';
import { kv } from '@vercel/kv';
import type { User } from '@/lib/db';
import { cookies } from 'next/headers';

export async function POST(req: NextRequest) {
  const cookieStore = cookies();
  const impersonatorId = cookieStore.get('impersonator_id')?.value;

  if (!impersonatorId) {
    return NextResponse.json({ message: 'No active impersonation session found' }, { status: 400 });
  }

  // Find the original admin user
  const adminUser: User | null = await kv.get(`readify:user:id:${impersonatorId}`);

  if (!adminUser || !adminUser.isAdmin) {
    // Clear cookies just in case, and redirect to login
    const response = NextResponse.redirect(new URL('/login', req.nextUrl));
    response.cookies.delete('session');
    response.cookies.delete('impersonator_id');
    return response;
  }

  // Create a new response so we can set cookies
  const response = NextResponse.json({ success: true });

  // Restore the admin's session
  await createSession(adminUser.id, adminUser.isAdmin, adminUser.username, response.cookies);

  // Clear the impersonator cookie
  response.cookies.delete('impersonator_id');

  return response;
}

src/app/api/auth/login/route.ts.content:
'use server';

import { kv } from '@vercel/kv';
import { NextRequest, NextResponse } from 'next/server';
import bcrypt from 'bcrypt';
import { createSession } from '@/lib/session';
import type { User } from '@/lib/db';

export async function POST(req: NextRequest) {
  try {
    const { email, password } = await req.json();

    // Use the new namespaced key
    const user: User | null = await kv.get(`readify:user:email:${email}`);

    if (!user) {
      console.log(`Login attempt failed: User not found for email ${email}`);
      return NextResponse.json({ message: 'Invalid credentials' }, { status: 401 });
    }

    // if user has not completed setup, they should not be able to log in with an empty password
    if (!user.password) {
      console.log(`Login attempt failed: Account not set up for email ${email}`);
      return NextResponse.json({ message: 'Account setup not complete. Please check your invitation email.' }, { status: 401 });
    }

    const passwordsMatch = await bcrypt.compare(password, user.password);

    if (!passwordsMatch) {
      console.log(`Login attempt failed: Password mismatch for email ${email}`);
      return NextResponse.json({ message: 'Invalid credentials' }, { status: 401 });
    }

    await createSession(user.id, user.isAdmin, user.username);

    let redirectUrl = user.isAdmin ? '/admin' : '/read';
    // If user has not set a username, redirect to the welcome page first
    if (!user.username) {
        redirectUrl = '/welcome';
    }

    console.log(`Login successful for ${email}, redirecting to ${redirectUrl}`);
    return NextResponse.json({ success: true, redirectUrl });

  } catch (error) {
    console.error('Login API Error:', error);
    const errorMessage = error instanceof Error ? error.message : 'An unexpected error occurred';
    return NextResponse.json({ message: errorMessage }, { status: 500 });
  }
}

